sail_code:
a!localVariables(
  local!defaultDecisionResponseOption: recordType!CMGT_WFL_CFG_TaskResponseOption(
    recordType!CMGT_WFL_CFG_TaskResponseOption.referenceTask: ri!newReferenceTask,
    recordType!CMGT_WFL_CFG_TaskResponseOption.isActive: true,
    recordType!CMGT_WFL_CFG_TaskResponseOption.label: ""
  ),
  {
    #"SYSTEM_SYSRULES_textField"(
      label: #"urn:appian:translation-string:v1:570e1c7d-29de-4357-8d92-a79bcbf922e0",
      value: ri!newReferenceTask[recordType!CMGT_WFL_CFG_ReferenceTask.nameLabel],
      saveInto: ri!newReferenceTask[recordType!CMGT_WFL_CFG_ReferenceTask.nameLabel],
      characterLimit: cons!CMGT_INT_MAX_CHAR_LIMIT_MEDIUM,
      showCharacterCount: false(),
      required: true,
      requiredMessage: #"urn:appian:translation-string:v1:4fab94dc-fcb1-427a-af9f-1239079b2897"(
        #"urn:appian:translation-variable:v1:3e31b6d5-df20-4d17-80a7-81f3c83ff370": #"urn:appian:translation-string:v1:570e1c7d-29de-4357-8d92-a79bcbf922e0"
      )
    ),
    /* Description */
    rule!CMGT_UTIL_RichTextField(
      value: ri!newReferenceTask[recordType!CMGT_WFL_CFG_ReferenceTask.description],
      readOnly: false(),
      label: #"urn:appian:translation-string:v1:0495bd73-7477-4bca-8918-2632c601a60b",
      helpTooltip: #"urn:appian:translation-string:v1:1540dd99-3992-4217-b534-43440de2fc7f"
    ),
    #"SYSTEM_SYSRULES_columnsLayout"(
      columns: {
        #"SYSTEM_SYSRULES_columnLayout"(
          contents: #"SYSTEM_SYSRULES_dropdownField_v1"(
            data: #"SYSTEM_SYSRULES_recordData"(
              recordType: recordType!CMGT_CFG_TaskBehaviorType,
              filters: #"SYSTEM_SYSRULES_queryLogicalExpression"(
                operator: "AND",
                filters: #"SYSTEM_SYSRULES_queryFilter"(
                  field: recordType!CMGT_CFG_TaskBehaviorType.taskBehaviorTypeId,
                  operator: "<>",
                  value: cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_AUTOMATION
                )
              )
            ),
            choiceLabels: recordType!CMGT_CFG_TaskBehaviorType.label,
            choiceValues: recordType!CMGT_CFG_TaskBehaviorType.taskBehaviorTypeId,
            label: #"urn:appian:translation-string:v1:87b80513-1e00-457f-92aa-c7df18190cd5",
            placeholder: #"urn:appian:translation-string:v1:88737b77-6273-41ec-bcc3-c41bacb44d4a",
            value: ri!newReferenceTask[recordType!CMGT_WFL_CFG_ReferenceTask.taskBehaviorTypeId],
            saveInto: /*Need to clear out other data that might have been set before changing task type*/
            {
              ri!newReferenceTask[recordType!CMGT_WFL_CFG_ReferenceTask.taskBehaviorTypeId],
              a!save(
                ri!newReferenceTask[recordType!CMGT_WFL_CFG_ReferenceTask.taskBehaviorType],
                rule!CMGT_QR_GetTaskBehaviorType(
                  returnType: "SINGLE_OBJECT",
                  taskBehaviorTypeId: save!value
                )
              ),
              /*Clear the response options out if the task type is not decision*/
              a!save(
                ri!newReferenceTask[recordType!CMGT_WFL_CFG_ReferenceTask.taskResponseOptions],
                if(
                  and(
                    ri!newReferenceTask[recordType!CMGT_WFL_CFG_ReferenceTask.taskBehaviorTypeId] = cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_DECISION,
                    rule!CMGT_UTIL_IsBlank(
                      ri!newReferenceTask[recordType!CMGT_WFL_CFG_ReferenceTask.taskResponseOptions]
                    )
                  ),
                  #"SYSTEM_SYSRULES_forEach"(
                    items: enumerate(2) + 1,
                    expression: local!defaultDecisionResponseOption
                  ),
                  null
                )
              ),
              /*Clear the question out if the task type is not decision*/
              if(
                ri!newReferenceTask[recordType!CMGT_WFL_CFG_ReferenceTask.taskBehaviorTypeId] <> cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_DECISION,
                a!save(
                  ri!newReferenceTask[recordType!CMGT_WFL_CFG_ReferenceTask.questionLabel],
                  null
                ),
                {}
              ),
              /*Clear the group assignee out if the task type is automated*/
              a!save(
                ri!newReferenceTask[recordType!CMGT_WFL_CFG_ReferenceTask.defaultGroupAssignee],
                if(
                  ri!newReferenceTask[recordType!CMGT_WFL_CFG_ReferenceTask.taskBehaviorType.isAutomated],
                  null,
                  ri!newReferenceTask[recordType!CMGT_WFL_CFG_ReferenceTask.defaultGroupAssignee]
                )
              ),
              /*Clear the SLA days out if the task type is automated*/
              a!save(
                ri!newReferenceTask[recordType!CMGT_WFL_CFG_ReferenceTask.defaultSla],
                if(
                  ri!newReferenceTask[recordType!CMGT_WFL_CFG_ReferenceTask.taskBehaviorType.isAutomated],
                  null,
                  ri!newReferenceTask[recordType!CMGT_WFL_CFG_ReferenceTask.defaultSla]
                )
              ),
              /*Clear the create case fields*/
              a!save(
                ri!newReferenceTask[recordType!CMGT_WFL_CFG_ReferenceTask.createCaseReferenceTask],
                {}
              ),
              /*Clear the send email reference task relationship, if the Task behavior type is not send email*/
              a!save(
                ri!newReferenceTask[recordType!CMGT_WFL_CFG_ReferenceTask.sendEmailReferenceTask],
                null
              ),
              if(
                rule!CMGT_UTIL_IsNotBlank(input: ri!currentTaskEmailTemplates),
                {
                  /* Append the newly created email templates attachmentIds to documentsToDelete */
                  a!save(
                    ri!documentsToDelete,
                    append(
                      ri!documentsToDelete,
                      rule!CMGT_UTIL_RejectBlank(
                        array: index(
                          ri!currentTaskEmailTemplates,
                          recordType!CMGT_WFL_CFG_EmailTemplate.emailTemplateAttachments.appianDocumentId,
                          null
                        )
                      )
                    )
                  ),
                  /* Clear all the newly created email Templates */
                  a!save(
                    ri!emailTemplates,
                    rule!CMGT_UTIL_Filter(
                      records: ri!emailTemplates,
                      field: recordType!CMGT_WFL_CFG_EmailTemplate.emailTemplateUuid,
                      value: ri!currentTaskEmailTemplates[recordType!CMGT_WFL_CFG_EmailTemplate.emailTemplateUuid],
                      filterType: "REJECT"
                    )
                  )
                },
                {}
              )
            },
            required: true,
            requiredMessage: #"urn:appian:translation-string:v1:4fab94dc-fcb1-427a-af9f-1239079b2897"(
              #"urn:appian:translation-variable:v1:3e31b6d5-df20-4d17-80a7-81f3c83ff370": #"urn:appian:translation-string:v1:87b80513-1e00-457f-92aa-c7df18190cd5"
            )
          )
        )
      },
      marginBelow: a!match(
        value: ri!newReferenceTask[recordType!CMGT_WFL_CFG_ReferenceTask.taskBehaviorType.taskBehaviorTypeId],
        whenTrue: a!isNullOrEmpty(fv!value),
        then: "EVEN_LESS",
        equals: cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_DOCUMENT_UPLOAD,
        then: "EVEN_LESS",
        equals: cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_DECISION,
        then: "STANDARD",
        equals: cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_CONFIRMATION,
        then: "EVEN_LESS",
        equals: cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_CREATE_CASE,
        then: "STANDARD",
        equals: cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_SEND_EMAIL,
        then: "STANDARD",
        default: "EVEN_LESS"
      )
    ),
    #"SYSTEM_SYSRULES_columnsLayout"(
      columns: {
        #"SYSTEM_SYSRULES_columnLayout"(
          contents: a!match(
            value: ri!newReferenceTask[recordType!CMGT_WFL_CFG_ReferenceTask.taskBehaviorTypeId],
            equals: cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_DECISION,
            then: {
              /* Question */
              #"SYSTEM_SYSRULES_textField"(
                label: #"urn:appian:translation-string:v1:1d75c2e0-04ea-4b38-97fd-62fb1a14f0f4",
                helpTooltip: #"urn:appian:translation-string:v1:2d58f145-74bd-433d-b33b-2d4f0568704b",
                value: ri!newReferenceTask[recordType!CMGT_WFL_CFG_ReferenceTask.questionLabel],
                saveInto: ri!newReferenceTask[recordType!CMGT_WFL_CFG_ReferenceTask.questionLabel],
                characterLimit: cons!CMGT_INT_MAX_CHAR_LIMIT_MEDIUM,
                showWhen: and(
                  a!isNotNullOrEmpty(
                    ri!newReferenceTask[recordType!CMGT_WFL_CFG_ReferenceTask.taskBehaviorTypeId]
                  ),
                  ri!newReferenceTask[recordType!CMGT_WFL_CFG_ReferenceTask.taskBehaviorTypeId] = cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_DECISION
                ),
                required: true,
                requiredMessage: #"urn:appian:translation-string:v1:4fab94dc-fcb1-427a-af9f-1239079b2897"(
                  #"urn:appian:translation-variable:v1:3e31b6d5-df20-4d17-80a7-81f3c83ff370": #"urn:appian:translation-string:v1:1d75c2e0-04ea-4b38-97fd-62fb1a14f0f4"
                )
              ),
              #"SYSTEM_SYSRULES_richTextDisplayField"(
                labelPosition: "COLLAPSED",
                value: #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: #"urn:appian:translation-string:v1:773c4e60-8a60-4dbf-834c-bb4ef201b786",
                  style: "STRONG"
                )
              ),
              #"SYSTEM_SYSRULES_forEach"(
                items: ri!newReferenceTask[recordType!CMGT_WFL_CFG_ReferenceTask.taskResponseOptions],
                expression: {
                  #"SYSTEM_SYSRULES_sideBySideLayout"(
                    alignVertical: "MIDDLE",
                    items: {
                      #"SYSTEM_SYSRULES_sideBySideItem"(
                        width: "MINIMIZE",
                        item: #"SYSTEM_SYSRULES_richTextDisplayField"(
                          value: #"SYSTEM_SYSRULES_richTextItem_v1"(text: fv!index, style: "STRONG")
                        )
                      ),
                      #"SYSTEM_SYSRULES_sideBySideItem"(
                        item: #"SYSTEM_SYSRULES_textField"(
                          label: #"urn:appian:translation-string:v1:9731a5c4-f9d3-439e-9256-2bead6cb98b7"(
                            #"urn:appian:translation-variable:v1:e5dab292-88a7-4032-b649-3c55fcb88189": fv!index
                          ),
                          labelPosition: "COLLAPSED",
                          requiredMessage: #"urn:appian:translation-string:v1:91314f57-1a7a-4999-88b8-7e6bab124c67",
                          value: fv!item[recordType!CMGT_WFL_CFG_TaskResponseOption.label],
                          saveInto: fv!item[recordType!CMGT_WFL_CFG_TaskResponseOption.label],
                          characterLimit: cons!CMGT_INT_MAX_CHAR_LIMIT_MEDIUM
                        )
                      ),
                      #"SYSTEM_SYSRULES_sideBySideItem"(
                        item: #"SYSTEM_SYSRULES_richTextDisplayField"(
                          labelPosition: "COLLAPSED",
                          value: {
                            #"SYSTEM_SYSRULES_richTextIcon"(
                              showWhen: rule!CMGT_UTIL_IsBlank(
                                fv!item[recordType!CMGT_WFL_CFG_TaskResponseOption.taskResponseOptionId]
                              ),
                              icon: "times",
                              color: "NEGATIVE",
                              size: "MEDIUM",
                              linkStyle: "STANDALONE",
                              link: #"SYSTEM_SYSRULES_dynamicLink"(
                                saveInto: if(
                                  a!isNullOrEmpty(
                                    fv!item[recordType!CMGT_WFL_CFG_TaskResponseOption.taskResponseOptionId]
                                  ),
                                  a!save(
                                    ri!newReferenceTask[recordType!CMGT_WFL_CFG_ReferenceTask.taskResponseOptions],
                                    remove(
                                      ri!newReferenceTask[recordType!CMGT_WFL_CFG_ReferenceTask.taskResponseOptions],
                                      fv!index
                                    )
                                  ),
                                  a!save(
                                    fv!item,
                                    #"urn:appian:function:v1:a:update"(
                                      fv!item,
                                      recordType!CMGT_WFL_CFG_TaskResponseOption.isActive,
                                      false
                                    )
                                  )
                                )
                              ),
                              altText: #"urn:appian:translation-string:v1:2a483bf9-c1f6-41dc-9d02-b9e65747a006"(
                                #"urn:appian:translation-variable:v1:ee534079-3e08-4c59-ae4d-ef3d8cfb6155": fv!index
                              )
                            )
                          }
                        ),
                        width: "MINIMIZE"
                      )
                    }
                  )
                }
              ),
              #"SYSTEM_SYSRULES_ButtonArrayLayout"(
                buttons: #"SYSTEM_SYSRULES_buttonWidget_v1"(
                  size: "SMALL",
                  icon: "plus-circle",
                  label: #"urn:appian:translation-string:v1:3efef77c-5d03-4add-b1a2-6fdf7f39d690",
                  saveInto: a!save(
                    ri!newReferenceTask[recordType!CMGT_WFL_CFG_ReferenceTask.taskResponseOptions],
                    append(
                      ri!newReferenceTask[recordType!CMGT_WFL_CFG_ReferenceTask.taskResponseOptions],
                      local!defaultDecisionResponseOption
                    )
                  ),
                  accessibilityText: #"urn:appian:translation-string:v1:9e159048-eba6-486b-9a68-0a8e9f01b555",
                  style: "LINK",
                  showWhen: count(
                    ri!newReferenceTask[recordType!CMGT_WFL_CFG_ReferenceTask.taskResponseOptions]
                  ) < 5,

                ),
                align: "START",
                marginAbove: "NONE",
                marginBelow: "NONE"
              )
            },
            equals: cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_CREATE_CASE,
            /*Create Case*/
            then: {
              #"SYSTEM_SYSRULES_dropdownField_v1"(
                label: #"urn:appian:translation-string:v1:28ea68dc-457f-4863-9e77-b36020dbeb4b",
                data: #"SYSTEM_SYSRULES_recordData"(
                  recordType: recordType!CMGT_CFG_CaseType,
                  filters: #"SYSTEM_SYSRULES_queryLogicalExpression"(
                    operator: "AND",
                    filters: {
                      #"SYSTEM_SYSRULES_queryFilter"(
                        field: recordType!CMGT_CFG_CaseType.isCategory,
                        operator: "=",
                        value: false
                      ),
                      #"SYSTEM_SYSRULES_queryFilter"(
                        field: recordType!CMGT_CFG_CaseType.isActive,
                        operator: "=",
                        value: true()
                      ),
                      #"SYSTEM_SYSRULES_queryFilter"(
                        field: recordType!CMGT_CFG_CaseType.areParentsActive,
                        operator: "=",
                        value: true()
                      )
                    }
                  )
                ),
                placeholder: #"urn:appian:translation-string:v1:d91269ae-78f6-4736-b22a-ea742a59e80d",
                choiceLabels: recordType!CMGT_CFG_CaseType.fullLabel,
                choiceValues: recordType!CMGT_CFG_CaseType.uuid,
                value: ri!newReferenceTask[recordType!CMGT_WFL_CFG_ReferenceTask.createCaseReferenceTask.caseTypeUuid],
                saveInto: ri!newReferenceTask[recordType!CMGT_WFL_CFG_ReferenceTask.createCaseReferenceTask.caseTypeUuid],
                required: true(),
                requiredMessage: #"urn:appian:translation-string:v1:4fab94dc-fcb1-427a-af9f-1239079b2897"(#"urn:appian:translation-variable:v1:3e31b6d5-df20-4d17-80a7-81f3c83ff370": #"urn:appian:translation-string:v1:28ea68dc-457f-4863-9e77-b36020dbeb4b")

              ),
              #"SYSTEM_SYSRULES_dropdownField_v1"(
                label: #"urn:appian:translation-string:v1:f967743d-71a3-4f23-8107-b8f1ebc38396",
                data: #"SYSTEM_SYSRULES_recordData"(
                  recordType: recordType!CMGT_CFG_RelationshipMapType,
                  filters: #"SYSTEM_SYSRULES_queryLogicalExpression"(
                    operator: "AND",
                    filters: {
                      #"SYSTEM_SYSRULES_queryFilter"(
                        field: recordType!CMGT_CFG_RelationshipMapType.type,
                        operator: "=",
                        value: "CASE_TO_CASE"
                      ),
                      #"SYSTEM_SYSRULES_queryFilter"(
                        field: recordType!CMGT_CFG_RelationshipMapType.isActive,
                        operator: "=",
                        value: true()
                      )
                    }
                  )
                ),
                placeholder: #"urn:appian:translation-string:v1:3831f890-1fc6-41e0-8732-dedfe0749174",
                choiceLabels: recordType!CMGT_CFG_RelationshipMapType.relationship.label,
                choiceValues: recordType!CMGT_CFG_RelationshipMapType.relationshipMapTypeId,
                value: ri!newReferenceTask[recordType!CMGT_WFL_CFG_ReferenceTask.createCaseReferenceTask.relationshipToOriginalCase],
                saveInto: ri!newReferenceTask[recordType!CMGT_WFL_CFG_ReferenceTask.createCaseReferenceTask.relationshipToOriginalCase]
              )
            },
            equals: cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_SEND_EMAIL,
            then: {
              #"SYSTEM_SYSRULES_sideBySideLayout"(
                items: {
                  #"SYSTEM_SYSRULES_sideBySideItem"(
                    item: #"SYSTEM_SYSRULES_dropdownField_v1"(
                      choiceLabels: ri!emailTemplates[recordType!CMGT_WFL_CFG_EmailTemplate.templateName],
                      choiceValues: ri!emailTemplates[recordType!CMGT_WFL_CFG_EmailTemplate.emailTemplateUuid],
                      label: #"urn:appian:translation-string:v1:9ee41a2b-9ebc-4f9a-8581-138e2e348ba3",
                      placeholder: #"urn:appian:translation-string:v1:6deffc1a-e4be-4a1b-a899-02820e6ca2bd",
                      value: ri!newReferenceTask[recordType!CMGT_WFL_CFG_ReferenceTask.sendEmailReferenceTask.emailTemplateUuid],
                      saveInto: {
                        ri!newReferenceTask[recordType!CMGT_WFL_CFG_ReferenceTask.sendEmailReferenceTask.emailTemplateUuid]
                      },
                      required: true,
                      requiredMessage: #"urn:appian:translation-string:v1:4fab94dc-fcb1-427a-af9f-1239079b2897"(
                        #"urn:appian:translation-variable:v1:3e31b6d5-df20-4d17-80a7-81f3c83ff370": #"urn:appian:translation-string:v1:9ee41a2b-9ebc-4f9a-8581-138e2e348ba3"
                      )
                    ),
                    width: "10X"
                  ),
                  #"SYSTEM_SYSRULES_sideBySideItem"(
                    item: #"SYSTEM_SYSRULES_ButtonArrayLayout"(
                      buttons: #"SYSTEM_SYSRULES_buttonWidget_v1"(
                        label: #"urn:appian:translation-string:v1:8081c5e8-c2e7-4294-af43-28e7a98658e6",
                        saveInto: a!save(ri!isPreview, true()),
                        disabled: rule!CMGT_UTIL_IsBlank(
                          value: ri!newReferenceTask[recordType!CMGT_WFL_CFG_ReferenceTask.sendEmailReferenceTask.emailTemplateUuid]
                        )
                      ),
                      align: "START",
                      marginAbove: "LESS",
                      marginBelow: "NONE"
                    ),
                    width: "MINIMIZE"
                  )
                },
                alignVertical: "BOTTOM",
                showWhen: tointeger(
                  ri!newReferenceTask[recordType!CMGT_WFL_CFG_ReferenceTask.taskBehaviorTypeId]
                ) = cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_SEND_EMAIL,
                marginAbove: "NONE",
                marginBelow: "NONE"
              ),
              #"SYSTEM_SYSRULES_ButtonArrayLayout"(
                buttons: #"SYSTEM_SYSRULES_buttonWidget_v1"(
                  label: #"urn:appian:translation-string:v1:e2e5d9f2-a4d2-45e2-8011-e195eaeb07f7",
                  icon: "plus-circle",
                  saveInto: a!save(
                    ri!currentForm,
                    "CREATE_NEW_EMAIL_TEMPLATE"
                  ),
                  style: "LINK",
                  showWhen: tointeger(
                    ri!newReferenceTask[recordType!CMGT_WFL_CFG_ReferenceTask.taskBehaviorTypeId]
                  ) = cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_SEND_EMAIL
                ),
                align: "START",
                marginAbove: "LESS",
                marginBelow: "NONE"
              ),

            },
            default: {}
          )
        )
      },
      marginBelow: a!match(
        value: ri!newReferenceTask[recordType!CMGT_WFL_CFG_ReferenceTask.taskBehaviorType.taskBehaviorTypeId],
        whenTrue: a!isNullOrEmpty(fv!value),
        then: "NONE",
        equals: cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_DOCUMENT_UPLOAD,
        then: "NONE",
        equals: cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_DECISION,
        then: "NONE",
        equals: cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_CONFIRMATION,
        then: "NONE",
        equals: cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_CREATE_CASE,
        then: "EVEN_LESS",
        equals: cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_SEND_EMAIL,
        then: "EVEN_LESS",
        default: "EVEN_LESS"
      )
    )
  }
)
