  local!bundle: rule!AS_GCM_CO_UT_loadBundleFromFolder(langISOCode: null()),
  local!configMaps: rule!AS_GCM_CEDM_typeConfigMapForDataMigration(),
  local!independentExportableMaps: rule!AS_CO_UT_indexPath(
    cdt: todatasubset(
      arrayToPage: rule!AS_GCM_filterRecords(
        records: local!configMaps,
        field: "isIndependentExportable",
        value: true
      ),
      pagingConfiguration: #"SYSTEM_SYSRULES_pagingInfo"(1, - 1, #"SYSTEM_SYSRULES_sortInfo"("displayName", true))
    ),
    field: "data",
    indexDefault: null
  ),
  local!exportChoices: #"SYSTEM_SYSRULES_forEach"(
    items: local!independentExportableMaps,
    expression: a!localVariables(
      local!count: rule!AS_CO_UT_queryRecord(
        recordType: fv!item.recordType,
        returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_TOTAL_COUNT
      ),
      if(local!count > 0, fv!item, {})
    )
  ),
  local!uuidsToExport: {},
  #"SYSTEM_SYSRULES_formLayout_v2"(
    titleBar: "Export Data",
    showTitleBarDivider: true,
    showButtonDivider: true,
    contents: #"SYSTEM_SYSRULES_columnsLayout"(
      columns: {
        #"SYSTEM_SYSRULES_columnLayout"(
          contents: #"SYSTEM_SYSRULES_checkboxField_v1"(
            label: "Which data sets would you like to export?",
            instructions: "Only datasets with data are listed above.",
            choiceLabels: local!exportChoices.displayName,
            choiceValues: local!exportChoices.uuid,
            value: local!uuidsToExport,
            saveInto: local!uuidsToExport,
            choiceStyle: "CARDS",
            required: true(),
            requiredMessage: rule!AS_CO_UT_displayRequiredMessage(
              label: "Export Data"
            )
          ),
          width: "MEDIUM_PLUS"
        )
      },
      spacing: "NONE"
    ),
    buttons: #"SYSTEM_SYSRULES_ButtonLayout"(
      primaryButtons: {
        #"SYSTEM_SYSRULES_buttonWidget_v1"(
          label: "Export",
          value: cons!AS_CO_ENUM_USER_ACTION_SUBMIT,
          disabled: rule!AS_CO_UT_isBlank(local!uuidsToExport),
          saveInto: {
            ri!userAction,
            a!save(ri!uuidsToExport, local!uuidsToExport)
          },
          submit: true,
          style: "SOLID"
        )
      },
      secondaryButtons: {
        #"SYSTEM_SYSRULES_buttonWidget_v1"(
          label: "Cancel",
          value: cons!AS_CO_ENUM_USER_ACTION_CANCEL,
          saveInto: ri!userAction,
          submit: true,
          style: "OUTLINE",
          validate: false
        )
      }
    )
  )
)

