rule!AS_CO_UT_distinct(
  a!localVariables(
    /*Retrieving dependentRecordMaps of parent Record*/
    local!dependentRecordMaps: a!flatten(
      #"SYSTEM_SYSRULES_forEach"(
        items: rule!AS_CO_UT_rejectNullValuesFromArray(
          a!flatten(
            index(
              rule!AS_GCM_filterRecords(
                records: ri!configMaps,
                field: "uuid",
                value: ri!parentRecordUuid,

              ),
              "dependentRecordUuids",
              {}
            )
          )
        ),
        /*Constructing and considering dependentRecordMaps same as precedentRecordMaps*/
        expression: a!map(
          precedentRecordUuid: fv!item,
          fields: rule!AS_GCM_filterRecords(
            records: ri!configMaps,
            field: "uuid",
            value: ri!parentRecordUuid
          ).primaryKeyField,
          thisRecordUuid: ri!parentRecordUuid
        )
      )
    ),
    local!filteredDependentRecordMaps: if(
      rule!AS_CO_UT_isNotBlank(local!dependentRecordMaps),
      rule!AS_GCM_filterRecords(
        records: local!dependentRecordMaps,
        field: "precedentRecordUuid",
        value: ri!fullQueryRecords,
        filterType: "REJECT"
      ),
      null
    ),
    /*Retrieving precedentRecordMaps of parent Record*/
    local!precedentRecordMaps: a!flatten(
      index(
        rule!AS_GCM_filterRecords(
          records: ri!configMaps,
          field: "uuid",
          value: ri!parentRecordUuid,

        ),
        "precedentRecordFieldMaps",
        {}
      )
    ),
    local!filteredPrecedentRecordMaps: if(
      rule!AS_CO_UT_isNotBlank(local!precedentRecordMaps),
      rule!AS_GCM_filterRecords(
        records: local!precedentRecordMaps,
        field: "precedentRecordUuid",
        value: ri!fullQueryRecords,
        filterType: "REJECT"
      ),
      null
    ),
    local!updatedPrecedentRecordMaps: if(
      and(
        rule!AS_CO_UT_isBlank(local!filteredDependentRecordMaps),
        rule!AS_CO_UT_isBlank(local!filteredPrecedentRecordMaps)
      ),
      null,
      #"SYSTEM_SYSRULES_forEach"(
        items: rule!AS_CO_UT_rejectNullValuesFromArray(
          array: rule!AS_GCM_filterRecords(
            records: {
              local!filteredDependentRecordMaps,
              local!filteredPrecedentRecordMaps
            },
            field: "precedentRecordUuid",
            value: ri!parentRecordUuid,
            filterType: "REJECT"
          )
        ),
        expression: rule!AS_CO_UT_updateCdtByKeyValues(
          cdt: fv!item,
          keyValueMap: a!localVariables(
            local!thisRecordConfig: rule!AS_GCM_filterRecords(
              records: ri!configMaps,
              field: "uuid",
              value: fv!item.thisRecordUuid
            ),
            local!precedentRecordConfig: rule!AS_GCM_filterRecords(
              records: ri!configMaps,
              field: "uuid",
              value: fv!item.precedentRecordUuid
            ),
            local!precedentIdentifiers: tointeger(
              rule!AS_CO_UT_distinct(
                rule!AS_CO_UT_rejectNullValuesFromArray(
                  apply(
                    index(
                      if(
                        rule!AS_CO_UT_isNotBlank(local!thisRecordConfig.recordType),
                        rule!AS_GCM_CO_UT_queryRecord(
                          executeWhen: if(
                            toboolean(
                              index(
                                local!precedentRecordConfig,
                                "exportWithoutParent",
                                true
                              )
                            ) = true,
                            true,
                            rule!AS_CO_UT_isNotBlank(ri!filterValuesFromParentRecord)
                          ),
                          recordType: local!thisRecordConfig.recordType,
                          fields: fv!item.fields,
                          returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_DATA_SUBSET,
                          pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
                            1,
                            cons!AS_GCM_CO_INT_QUERY_RECORD_MAX_BATCH_SIZE
                          ),
                          /*Applying below filter to get the only the records that are referenced in the parent record*/
                          filters: {
                            #"SYSTEM_SYSRULES_queryFilter"(
                              field: if(
                                rule!AS_CO_UT_isBlank(local!thisRecordConfig.foreignKeyField),
                                local!thisRecordConfig.primaryKeyField,
                                local!thisRecordConfig.foreignKeyField
                              ),
                              operator: "in",
                              value: ri!filterValuesFromParentRecord,
                              applyWhen: rule!AS_CO_UT_isNotBlank(ri!filterValuesFromParentRecord)
                            ),
                            index(
                              local!thisRecordConfig,
                              "exportFilters",
                              {}
                            )
                          }
                        ).data,
                        rule!AS_GAM_UT_queryEntityWithSelection(
                          entity: local!thisRecordConfig.dataStoreEntity,
                          entityType: local!thisRecordConfig.entityType,
                          selectionColumns: fv!item.fields,
                          /*Applying below filter to get the only the records that are referenced in the parent record*/
                          filters: #"SYSTEM_SYSRULES_queryFilter"(
                            field: if(
                              rule!AS_CO_UT_isBlank(local!thisRecordConfig.foreignKeyField),
                              local!thisRecordConfig.primaryKeyField,
                              local!thisRecordConfig.foreignKeyField
                            ),
                            operator: "in",
                            value: ri!filterValuesFromParentRecord,
                            applyWhen: rule!AS_CO_UT_isNotBlank(ri!filterValuesFromParentRecord)
                          ),
                          returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_DATA_SUBSET,
                          pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(startIndex: 1, batchSize: - 1)
                        ).data
                      ),
                      _,
                      null
                    ),
                    fv!item.fields
                  )
                )
              )
            ),
            a!map(
              precedentRecordIdentifiers: local!precedentIdentifiers,
              executeWhen: and(
                rule!AS_CO_UT_isNotBlank(local!precedentIdentifiers),
                if(
                  toboolean(
                    index(
                      local!precedentRecordConfig,
                      "exportWithoutParent",
                      true
                    )
                  ) = true,
                  true,
                  rule!AS_CO_UT_isNotBlank(ri!filterValuesFromParentRecord)
                )
              ),
              precedentRecordData: if(
                rule!AS_CO_UT_isNotBlank(local!precedentRecordConfig.recordType),
                rule!AS_GCM_CO_UT_queryRecord(
                  fields: a!flatten(local!precedentRecordConfig.fields),
                  recordType: local!precedentRecordConfig.recordType,
                  filters: {
                    #"SYSTEM_SYSRULES_queryFilter"(
                      field: if(
                        rule!AS_CO_UT_isBlank(
                          local!precedentRecordConfig.foreignKeyField
                        ),
                        local!precedentRecordConfig.primaryKeyField,
                        local!precedentRecordConfig.foreignKeyField
                      ),
                      operator: "in",
                      value: local!precedentIdentifiers
                    ),
                    index(
                      local!precedentRecordConfig,
                      "exportFilters",
                      {}
                    )
                  },
                  executeWhen: and(
                    rule!AS_CO_UT_isNotBlank(local!precedentIdentifiers),
                    if(
                      toboolean(
                        index(
                          local!precedentRecordConfig,
                          "exportWithoutParent",
                          true
                        )
                      ) = true,
                      true,
                      rule!AS_CO_UT_isNotBlank(ri!filterValuesFromParentRecord)
                    )
                  ),
                  returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_DATA_SUBSET,
                  pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
                    1,
                    cons!AS_GCM_CO_INT_QUERY_RECORD_MAX_BATCH_SIZE
                  )
                ).data,
                a!flatten(
                  #"SYSTEM_SYSRULES_forEach"(
                    items: rule!AS_GCM_UT_getIdentifiersInBatches(
                      batchingSize: 3,
                      identifiers: local!precedentIdentifiers,
                      returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY
                    ),
                    expression: rule!AS_CO_UT_queryEntity(
                      entity: local!precedentRecordConfig.dataStoreEntity,
                      entityType: local!precedentRecordConfig.entityType,
                      filters: #"SYSTEM_SYSRULES_queryFilter"(
                        field: if(
                          rule!AS_CO_UT_isBlank(
