_a-0000e40a-de32-8000-9ba8-011c48011c48_.sail_code:
cast(
  typeof(ri!cdt),
  if(

    and(
      rule!AS_CO_UT_isBlank(cleanwith(ri!field, ".[]")),
      rule!AS_CO_UT_isNotBlank(stripwith(ri!field, "1234567890")),
      not(
        /*Cannot do a "simple" update if its a dictionary list, map list, or the field itself is an array*/
        or(
          rule!AS_CO_UT_typeCompare(
            value1: ri!cdt,
            value2: rule!AS_CO_UT_returnNullDictionaryList()
          ),
          rule!AS_CO_UT_typeCompare(
            value1: ri!cdt,
            value2: {a!map()}
          ),
          and(
            /*Both the CDT to update and the field within the CDT are list types*/
            rule!AS_CO_UT_checkIfArrayType(
              typeOf: ri!cdt
            ),
            rule!AS_CO_UT_checkIfArrayType(
              typeOf: rule!AS_CO_UT_returnSingleTypeObject(
                typeOf: ri!cdt
              )[ri!field]
            )
          )
        )
      )
    ),

    /*Early exit for a simple field-update*/
    rule!AS_CO_UT_zINTERNAL_updateCdtFields_updateHandler(
      cdt: ri!cdt,
      field: ri!field,
      indices: ri!indices,
      newValue: ri!newValue
    ),

    with(
      local!fieldsAndIndices: rule!AS_CO_UT_zINTERNAL_indexPath_nestedFieldArrayHandler(field: ri!field),

      reduce(
        rule!AS_CO_UT_zINTERNAL_updateCdtFieldsByPath_nestHandler(
          previous: _,
          cdt: ri!cdt,
          fieldsAndIndices: local!fieldsAndIndices,
          /*Indices is only used when passing through from AS_CO_UT_updateCdtFields, never AS_CO_UT_updateCdtFieldsByPath*/
          indices: ri!indices,
          index: _
        ),
        ri!newValue,
        reverse(rule!AS_CO_UT_enumForCount(input: local!fieldsAndIndices))
      )
    )
  )
)

5f18e821-865e-464d-b974-57c20096c58f.sail_code:
if(
  rule!AS_CO_UT_isBlank(ri!identifiers),
  null(),
  a!localVariables(
    /* Batching size to divide the identifiers into batches */
    local!batchingSize: a!defaultValue(
      value: ri!batchingSize,
      default: cons!AS_GCM_INT_QUERY_BATCH_SIZE_TINY
    ),
    local!identifiers: if(
      and(
        ri!returnType = cons!AS_CO_ENUM_QE_RETURN_TYPE_DATA_SUBSET,
        ri!pagingInfo.batchSize <> - 1
      ),
      index(
        ri!identifiers,
        enumerate(
          a!defaultValue(
            value: ri!pagingInfo.batchSize,
            default: local!batchingSize
          )
        ) + a!defaultValue(
          value: ri!pagingInfo.startIndex,
          default: 1
        ),
        null()
      ),
      ri!identifiers
    ),
    /* To divide the whole identifiers to the total Batch Size */
    local!batches: if(
      rule!AS_CO_UT_isBlank(local!identifiers),
      null(),
      enumerate(
        ceiling(
          length(local!identifiers) / local!batchingSize
        )
      )
    ),
    #"SYSTEM_SYSRULES_forEach"(
      items: local!batches,
      expression: a!localVariables(
        local!itemCount: if(
          fv!item * local!batchingSize + local!batchingSize < length(local!identifiers),
          local!batchingSize,
          length(local!identifiers) - fv!item * local!batchingSize
        ),
        a!map(
          batch: fv!item,
          identifiers: index(
            local!identifiers,
            enumerate(local!itemCount) + fv!item * local!batchingSize + 1,
            null
          )
        )
      )
    )
  )
)

