            cons!AS_GCM_CO_INT_QUERY_RECORD_MAX_BATCH_SIZE
          ),
          local!loopCount: ceiling(count(local!data) / local!batchSize, 1),
          #"SYSTEM_SYSRULES_forEach"(
            items: enumerate(local!loopCount),
            expression: a!localVariables(
              local!dataSubset: todatasubset(
