_a-0000e40a-de32-8000-9ba8-011c48011c48_.sail_code:
cast(
  typeof(ri!cdt),
  if(

    and(
      rule!AS_CO_UT_isBlank(cleanwith(ri!field, ".[]")),
      rule!AS_CO_UT_isNotBlank(stripwith(ri!field, "1234567890")),
      not(
        /*Cannot do a "simple" update if its a dictionary list, map list, or the field itself is an array*/
        or(
          rule!AS_CO_UT_typeCompare(
            value1: ri!cdt,
            value2: rule!AS_CO_UT_returnNullDictionaryList()
          ),
          rule!AS_CO_UT_typeCompare(
            value1: ri!cdt,
            value2: {a!map()}
          ),
          and(
            /*Both the CDT to update and the field within the CDT are list types*/
            rule!AS_CO_UT_checkIfArrayType(
              typeOf: ri!cdt
            ),
            rule!AS_CO_UT_checkIfArrayType(
              typeOf: rule!AS_CO_UT_returnSingleTypeObject(
                typeOf: ri!cdt
              )[ri!field]
            )
          )
        )
      )
    ),

    /*Early exit for a simple field-update*/
    rule!AS_CO_UT_zINTERNAL_updateCdtFields_updateHandler(
      cdt: ri!cdt,
      field: ri!field,
      indices: ri!indices,
      newValue: ri!newValue
    ),

    with(
      local!fieldsAndIndices: rule!AS_CO_UT_zINTERNAL_indexPath_nestedFieldArrayHandler(field: ri!field),

      reduce(
        rule!AS_CO_UT_zINTERNAL_updateCdtFieldsByPath_nestHandler(
          previous: _,
          cdt: ri!cdt,
          fieldsAndIndices: local!fieldsAndIndices,
          /*Indices is only used when passing through from AS_CO_UT_updateCdtFields, never AS_CO_UT_updateCdtFieldsByPath*/
          indices: ri!indices,
          index: _
        ),
        ri!newValue,
        reverse(rule!AS_CO_UT_enumForCount(input: local!fieldsAndIndices))
      )
    )
  )
)

_a-0000e3e6-fb42-8000-9ba5-011c48011c48_.sail_code:
cast(
   'type!{http://www.appian.com/ae/types/2009}Boolean',
  with(
    local!typeNumber: rule!AS_CO_UT_returnTypeNumber(
      type: ri!type,
      typeOf: ri!typeOf
    ),

    and(
      /*The type of the input equals the type of the input returned as single type*/
      local!typeNumber = rule!AS_CO_UT_returnTypeNumber(
        typeOf: rule!AS_CO_UT_returnSingleTypeObject(typeOf: cast(local!typeNumber, null))
      ),

      /*And the type of the input is not list of variant*/
      local!typeNumber <> rule!AS_CO_UT_returnTypeNumber(
        typeOf: {}
      )
    )
  )
)

