  fn!error(concat("Null `recordType` passed to AS_CO_UT_queryRecord.  See documentation in AS_CO_UT_queryRecord for further information on how to use this parameter.")),
  and(
    ri!returnType = cons!AS_CO_ENUM_QE_RETURN_TYPE_DATA_SUBSET,
    rule!AS_CO_UT_isBlank(ri!pagingInfo)
  ),
  fn!error(concat("`pagingInfo` is required (cannot be null) for `returnType` DATA_SUBSET")),
  and(
    ri!returnType <> cons!AS_CO_ENUM_QE_RETURN_TYPE_DATA_SUBSET,
    rule!AS_CO_UT_isNotBlank(ri!pagingInfo)
  ),
  fn!error(concat("`pagingInfo` must be null when the `returnType` is not DATA_SUBSET, instead use `sort` to pass custom-sorting and `startIndex` & `batchSize` will be automatically calculated for you")),
  and(
    not(isnull(ri!pagingInfo)),
    rule!AS_CO_UT_isNotBlank(ri!sort)
  ),
  fn!error(concat("`sort` parameter must be null when `pagingInfo` is provided.  Use the sort field of the provided `pagingInfo` instead.")),
  and(
    ri!returnType = cons!AS_CO_ENUM_QE_RETURN_TYPE_AGGREGATION,
    rule!AS_CO_UT_isBlank(ri!aggregationFields)
  ),
  fn!error("You must provide an input for `aggregationFields` when `returnType` is AGGREGATION"),
  and(
    ri!returnType = cons!AS_CO_ENUM_QE_RETURN_TYPE_AGGREGATION,
    rule!AS_CO_UT_isNotBlank(ri!fields)
  ),
  fn!error("You cannot provide an input for `fields` when `returnType` is AGGREGATION.  Use `aggregationFields` instead."),
  and(
    ri!returnType <> cons!AS_CO_ENUM_QE_RETURN_TYPE_AGGREGATION,
    rule!AS_CO_UT_isNotBlank(ri!aggregationFields)
  ),
  fn!error("You cannot provide an input for `aggregationFields` when `returnType` is not AGGREGATION.  Use `fields` instead."),
  and(
    rule!AS_CO_UT_isNotBlank(ri!castToCdtType),
    not(contains(
      {cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY, cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT, cons!AS_CO_ENUM_QE_RETURN_TYPE_DATA_SUBSET},
      ri!returnType
