    /*Determine the fetchTotalCount*/
    local!fetchTotalCount: rule!AS_CO_UT_displayValue(
      value: ri!returnType,
      inArray: rule!AS_CO_CONS_QE_RETURN_TYPES(),
      replacement: {
        /*DATA_SUBSET*/   true,
        /*SINGLE_OBJECT*/ false,
        /*OBJECT_ARRAY*/  false,
        /*AGGREGATION*/   false,
        /*TOTAL_COUNT*/   true,
      }
    ),

    /*Execute the query*/
    local!queryResult: a!refreshVariable(
      value: if(
        ri!executeWhen = false,
        null(
          if(
            ri!returnType = cons!AS_CO_ENUM_QE_RETURN_TYPE_AGGREGATION,
            rule!AS_CO_TYPE_Dictionary(),
            ri!recordType
          )
        ),
        #"SYSTEM_SYSRULES_queryRecordType_v1"(
          recordType: ri!recordType,
          fields: a!defaultValue(
            ri!fields,
            ri!aggregationFields
          ),
          filters: #"SYSTEM_SYSRULES_queryLogicalExpression"(
            operator: "AND",
            logicalExpressions: {
              #"SYSTEM_SYSRULES_queryLogicalExpression"(
                operator: "AND",
                ignoreFiltersWithEmptyValues: true,
                filters: a!defaultValue(ri!filters, {})
              ),
              a!defaultValue(ri!logicalExpression, {})
            }
          ),
          pagingInfo: local!pagingInfo,
          fetchTotalCount: local!fetchTotalCount,
          relatedRecordData: ri!relatedRecordData
        )
      ),
      refreshOnVarChange: ri!triggerRefresh,
      refreshAfter: {"RECORD_ACTION"}
    ),

