a!localVariables(
  local!configMaps: rule!AS_GCM_CEDM_typeConfigMapForDataMigration(),
  #"urn:appian:function:v1:a:update"(
    ri!configData,
    "records",
    a!flatten(
      #"SYSTEM_SYSRULES_forEach"(
        items: ri!configData.records,
        expression: a!localVariables(
          local!configMap: rule!AS_GCM_filterRecords(
            records: local!configMaps,
            field: "uuid",
            value: fv!item.uuid
          )[1],
          local!constructPrimaryKeyMap: or(
            rule!AS_CO_UT_isBlank(fv!item.primaryKeyMaps),
            rule!AS_CO_UT_ContainsValueByField(
              record: fv!item.primaryKeyMaps,
              field: "thisEnvPrimaryKey",
              value: null
            )
          ),
          local!fileData: fv!item.data,
          local!logicalExpression: #"SYSTEM_SYSRULES_queryLogicalExpression"(
            operator: "OR",
            logicalExpressions: a!flatten(
              {
                if(
                  rule!AS_CO_UT_isNotBlank(
                    local!configMap.primaryIdentifierFields
                  ),
                  #"SYSTEM_SYSRULES_queryLogicalExpression"(
                    operator: "AND_ALL",
                    filters: #"SYSTEM_SYSRULES_forEach"(
                      items: local!configMap.primaryIdentifierFields,
                      expression: #"SYSTEM_SYSRULES_queryFilter"(
                        field: fv!item,
                        operator: "in",
                        value: a!flatten(
                          rule!AS_CO_UT_distinct({ index(local!fileData, fv!item, null) })
                        ),
                        applyWhen: rule!AS_CO_UT_isNotBlank(
                          rule!AS_CO_UT_rejectNullValuesFromArray({ index(local!fileData, fv!item, {}) })
                        )
                      )
                    )
                  ),
                  {}
                ),
                if(
                  rule!AS_CO_UT_isNotBlank(
                    local!configMap.secondaryIdentifierFields
                  ),
                  #"SYSTEM_SYSRULES_queryLogicalExpression"(
                    operator: "AND_ALL",
                    filters: #"SYSTEM_SYSRULES_forEach"(
                      items: local!configMap.secondaryIdentifierFields,
                      expression: #"SYSTEM_SYSRULES_queryFilter"(
                        field: fv!item,
                        operator: "in",
                        value: a!flatten(
                          if(
                            fv!item = recordType!AS GCM R Clause Section Mapping.refSection.contractFormatId,
                            {
                              tointeger(
                                rule!AS_CO_UT_distinct(
                                  rule!AS_CO_UT_rejectNullValuesFromArray({ index(local!fileData, fv!item, {}) })
                                )
                              )
                            },
                            {
                              rule!AS_CO_UT_distinct({ index(local!fileData, fv!item, null) })
                            }
                          )
                        ),
                        applyWhen: rule!AS_CO_UT_isNotBlank(
                          rule!AS_CO_UT_rejectNullValuesFromArray({ index(local!fileData, fv!item, {}) })
                        )
                      )
                    )
                  ),
                  {}
                )
              }
            )
          ),
          local!thisEnvData: if(
            a!isNullOrEmpty(local!configMap.recordType),
            rule!AS_GAM_UT_queryEntityWithSelection(
              entity: local!configMap.dataStoreEntity,
              entityType: local!configMap.entityType,
              selectionColumns: {
                local!configMap.primaryKeyField,
                local!configMap.primaryIdentifierFields,
                local!configMap.secondaryIdentifierFields
              },
              logicalExpression: local!logicalExpression,
              executeWhen: local!constructPrimaryKeyMap,
              returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_DATA_SUBSET,
              pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(startIndex: 1, batchSize: - 1)
            ).data,
            rule!AS_CO_UT_queryRecord(
              recordType: local!configMap.recordType,
              returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_DATA_SUBSET,
              executeWhen: local!constructPrimaryKeyMap,
              logicalExpression: local!logicalExpression,
              pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(startIndex: 1, batchSize: 5000),
              fields: {
                local!configMap.primaryKeyField,
                local!configMap.primaryIdentifierFields,
                local!configMap.secondaryIdentifierFields
              }
            ).data
          ),
          rule!AS_CO_UT_updateCdtFieldsByFieldValuePairs(
            cdt: fv!item,
            fieldValuePairs: {
              if(
                local!constructPrimaryKeyMap,
                a!map(
                  field: "primaryKeyMaps",
                  value: a!flatten(
                    #"SYSTEM_SYSRULES_forEach"(
                      items: local!fileData,
                      expression: a!localVariables(
                        local!currentFileData: fv!item,
                        local!currentThisEnvData: rule!AS_GCM_CEDM_filterDataByIdentifierFields(
                          configMap: local!configMap,
                          thisEnvData: local!thisEnvData,
                          currentFileData: local!currentFileData
                        ),
                        a!map(
                          originalPrimaryKey: local!currentFileData[local!configMap.primaryKeyField],
                          thisEnvPrimaryKey: local!currentThisEnvData[local!configMap.primaryKeyField]
                        )
                      )
                    )
                  )
                ),
                {}
              )
            }
          )
        )
      )
    )
  )
)

