    )
  )
)

_a-0000eae2-5167-8000-9ce4-011c48011c48_.sail_code:
rule!AS_CO_UT_whereContains(
  values: ri!value,
  array: if(
    /*Checking if the data is of type record*/
    rule!AS_CO_UT_isRecordTypeData(data: ri!cdtOrRecord),
    index(
      a!flatten(ri!cdtOrRecord),
      ri!field,
      null
    ),
    /*Using indexPath to index the data since its not record type data   */
    rule!AS_CO_UT_indexPath(
      cdt: a!flatten(ri!cdtOrRecord),
      field: ri!field,
      indexDefault: null
    )
  )
)
