  null(),
  a!localVariables(
    /* Batching size to divide the identifiers into batches */
    local!batchingSize: a!defaultValue(
      value: ri!batchingSize,
      default: cons!AS_GCM_INT_QUERY_BATCH_SIZE_TINY
    ),
    local!identifiers: if(
      and(
        ri!returnType = cons!AS_CO_ENUM_QE_RETURN_TYPE_DATA_SUBSET,
        ri!pagingInfo.batchSize <> - 1
      ),
      index(
        ri!identifiers,
        enumerate(
          a!defaultValue(
            value: ri!pagingInfo.batchSize,
            default: local!batchingSize
          )
        ) + a!defaultValue(
          value: ri!pagingInfo.startIndex,
          default: 1
        ),
        null()
      ),
      ri!identifiers
    ),
    /* To divide the whole identifiers to the total Batch Size */
    local!batches: if(
      rule!AS_CO_UT_isBlank(local!identifiers),
      null(),
      enumerate(
        ceiling(
          length(local!identifiers) / local!batchingSize
        )
      )
    ),
    #"SYSTEM_SYSRULES_forEach"(
      items: local!batches,
      expression: a!localVariables(
        local!itemCount: if(
          fv!item * local!batchingSize + local!batchingSize < length(local!identifiers),
          local!batchingSize,
          length(local!identifiers) - fv!item * local!batchingSize
        ),
        a!map(
          batch: fv!item,
          identifiers: index(
            local!identifiers,
            enumerate(local!itemCount) + fv!item * local!batchingSize + 1,
            null
          )
        )
      )
    )
  )
)

