a!match(
  value: null,
  whenTrue: and(
    rule!AS_CO_UT_booleanDefaultFalse(ri!useQueryRecord),
    rule!AS_CO_UT_isBlank(ri!setId)
  ),
  then: rule!AS_CO_UT_queryRecord(
    returnType: ri!returnType,
    triggerRefresh: ri!triggerRefresh,
    recordType: recordType!AS GCM Clause Set,
    executeWhen: ri!executeWhen,
    fields: {
      if(
        ri!excludeRelatedRecords,
        {},
        {
          recordType!AS GCM Clause Set.contractTypes,
          /*  Reference data    */
          recordType!AS GCM Clause Set.refStatus,
          recordType!AS GCM Clause Set.refContractMethod,
          recordType!AS GCM Clause Set.refCategory,
          recordType!AS GCM Clause Set.refType
        }
      ),
      a!defaultValue(ri!fields, {})
    },
    relatedRecordData: {
      #"SYSTEM_SYSRULES_relatedRecordData"(
        relationship: recordType!AS GCM Clause Set.contractTypes,
        limit: 10,
        filters: #"SYSTEM_SYSRULES_queryFilter"(
          field: recordType!AS GCM Clause Set Contract Type Map.isActive,
          operator: "=",
          value: true
        )
      ),
      #"SYSTEM_SYSRULES_relatedRecordData"(
        relationship: recordType!AS GCM Clause Set.clauses,
        limit: 10,
        filters: #"SYSTEM_SYSRULES_queryLogicalExpression"(
          operator: "OR",
          filters: {
            #"SYSTEM_SYSRULES_queryFilter"(
              field: recordType!AS GCM Clause Set Clause Synced.isActive,
              operator: "=",
              value: true
            ),
            #"SYSTEM_SYSRULES_queryFilter"(
              field: recordType!AS GCM Clause Set Clause Synced.refUsageId,
              operator: "=",
              value: cons!AS_GCM_REF_ID_USAGE_EXCLUDED
            )
          }
        )
      )
    },
    logicalExpression: ri!logicalExpression,
    filters: ri!additionalFilters,
    pagingInfo: ri!pagingInfo,
    sort: ri!sort,
    aggregationFields: ri!aggregation
  ),
  whenTrue: and(
    rule!AS_CO_UT_booleanDefaultFalse(ri!useQueryRecord),
    rule!AS_CO_UT_isNotBlank(ri!setId),
    all(
      rule!AS_CO_UT_isBlank(_),
      {
        ri!additionalFilters,
        ri!logicalExpression,
        ri!aggregation
      }
    )
  ),
  then: rule!AS_GCM_UT_queryRecordByIdentifier(
    refreshInterval: ri!refreshInterval,
    triggerRefresh: ri!triggerRefresh,
    recordType: recordType!AS GCM Clause Set,
    identifier: ri!setId,
    executeWhen: ri!executeWhen,
    fields: {
      if(
        ri!excludeRelatedRecords,
        {},
        {
          recordType!AS GCM Clause Set.contractTypes,
          /*  Reference data    */
          recordType!AS GCM Clause Set.refStatus,
          recordType!AS GCM Clause Set.refContractMethod,
          recordType!AS GCM Clause Set.refCategory,
          recordType!AS GCM Clause Set.refType
        }
      ),
      a!defaultValue(ri!fields, {})
    },
    relatedRecordData: {
      #"SYSTEM_SYSRULES_relatedRecordData"(
        relationship: recordType!AS GCM Clause Set.contractTypes,
        limit: 100,
        filters: #"SYSTEM_SYSRULES_queryFilter"(
          field: recordType!AS GCM Clause Set Contract Type Map.isActive,
          operator: "=",
          value: true
        )
      ),
      #"SYSTEM_SYSRULES_relatedRecordData"(
        relationship: recordType!AS GCM Clause Set.clauses,
        limit: 100,
        filters: #"SYSTEM_SYSRULES_queryLogicalExpression"(
          operator: "OR",
          filters: {
            #"SYSTEM_SYSRULES_queryFilter"(
              field: recordType!AS GCM Clause Set Clause Synced.isActive,
              operator: "=",
              value: true
            ),
            #"SYSTEM_SYSRULES_queryFilter"(
              field: recordType!AS GCM Clause Set Clause Synced.refUsageId,
              operator: "=",
              value: cons!AS_GCM_REF_ID_USAGE_EXCLUDED
            )
          }
        )
      )
    }
  ),
  default: rule!AS_CO_UT_queryEntity(
    entity: cons!AS_GCM_ENT_CLAUSE_SET,
    entityType: 'type!{urn:com:appian:types:AS:GCM}AS_GCM_ClauseSet',
    returnType: ri!returnType,
    executeWhen: and(
      ri!executeWhen,
      or(
        any(
          rule!AS_CO_UT_isNotBlank(_),
          {
            ri!additionalFilters,
            ri!logicalExpression,
            ri!aggregation
          }
        ),
        rule!AS_CO_UT_isNotBlank(input: ri!setId),
        rule!AS_CO_UT_isNotBlank(input: ri!solicitationId),
        rule!AS_CO_UT_isNotBlank(input: ri!awardId)
      )
    ),
    /* To ensure proper null checks, we'll force all queryFilter values to not be null */
    logicalExpression: ri!logicalExpression,
    pagingInfo: ri!pagingInfo,
    sort: ri!sort,
