_a-0000e915-02bc-8000-9c1f-011c48011c48_.sail_code:
a!localVariables(
  /* Removing the table fill-ins */
  local!oldValue: regexreplaceall(
    "<table.*?</table>",
    stripwith(ri!oldValue, char(10) & char(9)),
    ""
  ),
  local!newValue: regexreplaceall("<table.*?</table>", stripwith(ri!newValue,char(10)&char(9)), ""),
  /* Getting all the cite tags */
  local!newciteTag: regexallmatches(
    "class=""cite (.*?) ",
    local!newValue,
    "gms"
  ),
  local!oldciteTag: regexallmatches(
    "class=""cite (.*?) ",
    local!oldValue,
    "gms"
  ),
  /* Getting the count for added and removed fill ins based on difference b/w newCiteTag and oldCiteTag */
  a!map(
    addedCount: length(
      difference(local!newciteTag, local!oldciteTag)
    ),
    removedCount: length(
      difference(local!oldciteTag, local!newciteTag)
    )
  )
)

