      additionalFilters: {
        #"SYSTEM_SYSRULES_queryFilter"(
          field: "auditActionCode",
          operator: "<>",
          value: cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_UNCHANGED
        )
      }
    ),
    field: "data.relationAuditId"
  ),
  local!clauseSetClauses: rule!AS_GAM_UT_queryEntityWithSelection(
    entity: cons!AS_GCM_ENT_CLAUSE_SET_CLAUSE,
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
    filters: {
      #"SYSTEM_SYSRULES_queryFilter"(
        field: "setId",
        operator: "=",
        value: ri!clauseSet.setId
      ),
      #"SYSTEM_SYSRULES_queryFilter"(
        field: "isActive",
        operator: "in",
        value: { true(), false() }
      )
    },
    selectionColumns: {
      "relationId",
      "clauseTitle",
      "alternateValue",
      "refClause.clauseNumber",
      "refClause.deviation"
    },
    pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(startIndex: 1, batchSize: - 1),
    executeWhen: rule!AS_CO_UT_isNotBlank(ri!clauseSet.setId)
  ).data,
  local!contractTypeAudits: index(
    rule!AS_GCM_QE_getClauseSetContractTypeMapAudits(
      /*Having the batchSize as 50 because contractTypes might not exceed more than 50 and also to boost the performance a bit */
      pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(1, 50),
      clauseSetAuditId: ri!clauseSetAudit.clauseSetAuditId,
      /*the query should be executed only when the clauseSetAuditId is not blank*/
      executeWhen: rule!AS_CO_UT_isNotBlank(ri!clauseSetAudit.clauseSetAuditId),
      additionalFilters: {
        #"SYSTEM_SYSRULES_queryFilter"(
          field: "auditActionCode",
          operator: "<>",
          value: cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_UNCHANGED
        )
      }
    ),
    "data",
    null()
  ),
  local!mappingAuditIds: rule!AS_CO_UT_indexPath(
    cdt: local!contractTypeAudits,
    field: "mappingAuditId"
  ),
  local!contractTypeMaps: rule!AS_GCM_QE_getClauseSetContractTypeDetails(
    setId: ri!clauseSet.setId,
    executeWhen: rule!AS_CO_UT_isNotBlank(ri!clauseSet.setId),
    showInactive: true()
  ),
  local!templateChanges: rule!AS_GCM_QE_getClauseSetTemplateAudits(
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT,
    clauseSetAuditId: ri!clauseSetAudit.clauseSetAuditId,
    executeWhen: true
  ).simpleFieldChanges,
  local!copiedClauseSet: cast(
    'type!{urn:com:appian:types:AS:GCM}AS_GCM_ClauseSet',
    rule!AS_GAM_UT_queryEntityWithSelection(
      entity: cons!AS_GCM_ENT_CLAUSE_SET,
      pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(1, 1),
      filters: #"SYSTEM_SYSRULES_queryFilter"(
        field: "setId",
        operator: "=",
        value: ri!clauseSet.copiedSetId
      ),
      selectionColumns: { "setId", "setNumber", "categoryNumber" },
      executeWhen: a!isNotNullOrEmpty(ri!clauseSet.copiedSetId)
    ).data
  ),
  #"SYSTEM_SYSRULES_richTextDisplayField"(
    value: {
      rule!AS_GCM_DSP_displayAuditActionCodeText(
        actionCode: ri!clauseSetAudit.auditActionCode,
        bundle: ri!bundle
      ),
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: ""
      ),
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: {
          " ",
          a!match(
            value: ri!clauseSetAudit.auditActionCode,
            equals: cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_FINALIZE,
            then: "",
            equals: cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_FINALIZATION_FAILED,
            then: "",
            equals: cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_ADD,
            then: "from Contract Writing",
            equals: cons!AS_GCM_ADT_ENUM_AUDIT_ACTION_CODE_COPY,
            then: {
              "",
              char(32),
              if(
                and(
                  rule!AS_CO_UT_isNotBlank(ri!clauseSet.copiedSetId),
                  ri!isContractWriting <> true
                ),
                rule!AS_GCM_LNK_clauseSetRecordLink(
                  clauseSet: local!copiedClauseSet,
                  returnOnlyRichTextItem: true,
                  bundle: ri!bundle
                ),
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: rule!AS_GCM_DSP_clauseSetRecordTitle(
                    setNumber: a!defaultValue(
                      value: local!copiedClauseSet.setNumber,
                      default: rule!AS_CO_UT_filterCdtByField(
                        cdt: ri!clauseSetAudit.simpleFieldChanges,
                        field: "fieldName",
                        value: "copiedSetNumber"
                      ).newValue
                    ),
                    categoryNumber: local!copiedClauseSet.categoryNumber
                  )
                )
              )
            },
            default: {}
          )
        },
        showWhen: or(
