          and(
            ri!clauseSetAudit.auditActionCode = cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_ADD,
            length(
              {
                ri!clauseSet.awardId,
                ri!clauseSet.solicitationId
              }
            ) > 0
          )
        )
      ),
      char(10),
      char(10),
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: {
          if(
            or(
              ri!clauseSetAudit.auditActionCode = cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_ADD,
              ri!clauseSetAudit.auditActionCode = cons!AS_GCM_ADT_ENUM_AUDIT_ACTION_CODE_COPY
            ),
            {
              #"SYSTEM_SYSRULES_richTextBulletedList"(
                items: #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: {
                    #"SYSTEM_SYSRULES_richTextItem_v1"(
