                      )
                    )
                  }
                ),
                showWhen: rule!AS_CO_UT_isNotBlank(
                  input: ri!clauseSetAudit.simpleFieldChanges
                )
              ),
              /*Complex Fields*/
              /* Clauses and contract types*/
              rule!AS_GCM_DSP_displayModificationCommentForClauseSetComplexFields(
                bundle: ri!bundle,
                clauseSetClauses: local!clauseSetClauses,
                refData: ri!refData,
                refSection: ri!refSection,
                relationAuditIds: local!relationAuditIds,
                contractTypeMaps: local!contractTypeMaps,
                mappingAuditIds: local!mappingAuditIds,
                contractTypeAudits: local!contractTypeAudits
              )
            },
            /*Log changes if Clause was edited*/
            if(
              or(
                ri!clauseSetAudit.auditActionCode = cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_MODIFY,
                ri!clauseSetAudit.auditActionCode = cons!AS_GCM_ADT_ENUM_AUDIT_ACTION_CODE_VALIDATION_UPDATE
              ),
              {
                #"SYSTEM_SYSRULES_richTextBulletedList"(
                  items: {
                    /*Simple Fields*/
                    #"SYSTEM_SYSRULES_forEach"(
                      items: rule!AS_CO_UT_rejectNullValuesFromArray(
                        {
                          rule!AS_CO_UT_indexPath(
                            cdt: ri!clauseSetAudit,
                            field: "simpleFieldChanges"
                          ),
                          local!templateChanges
                        }
                      ),
                      expression: rule!AS_GCM_UT_constructAuditCommentForClauseSetFields(
                        fieldName: fv!item.fieldName,
                        oldValue: fv!item.oldValue,
                        newValue: fv!item.newValue,
                        refData: ri!refData,
                        refSection: ri!refSection,
                        bundle: ri!bundle
                      )
                    )
                  },
                  showWhen: if(
                    and(
                      rule!AS_CO_UT_isBlank(ri!clauseSetAudit.simpleFieldChanges),
                      rule!AS_CO_UT_isBlank(local!templateChanges)
                    ),
                    false,
                    true
                  )
                ),
                /*Complex Fields*/
                /* Clauses and contract types*/
                rule!AS_GCM_DSP_displayModificationCommentForClauseSetComplexFields(
                  bundle: ri!bundle,
                  clauseSetClauses: local!clauseSetClauses,
                  refData: ri!refData,
                  refSection: ri!refSection,
                  relationAuditIds: local!relationAuditIds,
                  contractTypeMaps: local!contractTypeMaps,
                  mappingAuditIds: local!mappingAuditIds,
                  contractTypeAudits: local!contractTypeAudits
                )
              },
              if(
                or(
                  ri!clauseSetAudit.auditActionCode = cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_VALIDATE,
                  ri!clauseSetAudit.auditActionCode = cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_VALIDATION_FAILED,
                  ri!clauseSetAudit.auditActionCode = cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_FINALIZATION_FAILED,
                  ri!clauseSetAudit.auditActionCode = cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_FINALIZE
                ),
                {
                  if(
                    ri!clauseSetAudit.auditActionCode = cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_FINALIZE,
