                  if(
                    ri!clauseSetAudit.auditActionCode = cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_VALIDATE,
                    #"SYSTEM_SYSRULES_richTextItem_v1"(
                      text: "",
                      color: cons!AS_GCM_HEX_CODE_AUDIT_ADDED_GREEN,
