                    mappingAuditIds: local!mappingAuditIds,
                    contractTypeAudits: local!contractTypeAudits
                  )
                },
                {}
              )
            )
          )
        }
      )
    },
    showWhen: or(
      ri!clauseSetAudit.auditActionCode = cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_VALIDATE,
      ri!clauseSetAudit.auditActionCode = cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_VALIDATION_FAILED,
