_a-0000e915-02bc-8000-9c1f-011c48011c48_.sail_code:
if(
  ri!showWhen,
  a!localVariables(
    /* Get the count of removed and added fill-ins*/
    local!getCount: rule!AS_GCM_UT_getRemovedAndAddedCountOfFillinsForAudit(
      oldValue: ri!oldValue,
      newValue: ri!newValue
    ),
    #"SYSTEM_SYSRULES_richTextItem_v1"(
      text: {
        /*If any fill-ins added*/
        #"SYSTEM_SYSRULES_richTextItem_v1"(
          text: {
            #"SYSTEM_SYSRULES_richTextItem_v1"(
              text: "",
              color: cons!AS_GCM_HEX_CODE_AUDIT_ADDED_GREEN,
              style: "STRONG"
            ),
            #"SYSTEM_SYSRULES_richTextItem_v1"(
              text: {
                char(32) & local!getCount.addedCount & char(32) & "Fill-in(s)"
              }
            )
          },
          showWhen: local!getCount.addedCount > 0
        ),
        #"SYSTEM_SYSRULES_richTextItem_v1"(
          text: {
            char(32),
            "",
            char(32)
          },
          showWhen: and(
            local!getCount.addedCount > 0,
            local!getCount.removedCount > 0
          )
        ),
        /*If no fill-ins added but removed*/
        #"SYSTEM_SYSRULES_richTextItem_v1"(
          text: {
            #"SYSTEM_SYSRULES_richTextItem_v1"(
              text: "",
              color: cons!AS_GCM_HEX_CODE_AUDIT_REMOVED_RED,
              style: "STRONG"
            ),
            #"SYSTEM_SYSRULES_richTextItem_v1"(
              text: {
                char(32) & local!getCount.removedCount & char(32) & "Fill-in(s)"
              }
            )
          },
          showWhen: local!getCount.removedCount > 0
        )
      },
      showWhen: or(
        local!getCount.addedCount > 0,
        local!getCount.removedCount > 0
      )
    ),

  ),
  {}
)

_a-0000e63a-a5cb-8000-9bc6-011c48011c48_.sail_code:
rule!AS_CO_UT_queryRecord(
  recordType: recordType!AS GCM R Template,
  returnType: a!defaultValue(
    value: ri!returnType,
    default: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY
  ),
  executeWhen: and(
    ri!executeWhen,
    or(
      any(
        rule!AS_CO_UT_isNotBlank(_),
        {
          ri!additionalFilters,
          ri!logicalExpression,
          ri!aggregationFields,
          ri!fields
        }
      ),
      ri!getAllRecords,
      rule!AS_CO_UT_isNotBlank(ri!templateIds)
    )
  ),
  /* To ensure proper null checks, we'll force all queryFilter values to not be null */
  logicalExpression: ri!logicalExpression,
  pagingInfo: ri!pagingInfo,
  triggerRefresh: ri!triggerRefresh,
  filters: {
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!AS GCM R Template.templateId,
      operator: "in",
      value: ri!templateIds,
      applyWhen: rule!AS_CO_UT_isNotBlank(ri!templateIds)
    ),
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!AS GCM R Template.isActive,
      operator: "=",
      value: true,
      applyWhen: and(ri!getAllRecords <> true,rule!AS_CO_UT_isBlank(ri!templateIds))
    ),
    ri!additionalFilters
  },
  fields: rule!AS_CO_UT_rejectNullValuesFromArray(
    {
      ri!fields,
      if(
        ri!includeRelatedRecords,
        {
          recordType!AS GCM R Template.templateClauses
        },
        {}
      )
    }
  ),
  aggregationFields: ri!aggregationFields,
  sort: ri!sort
)

