      rule!AS_CO_I18N_UT_getUserLocale(user: loggedInUser())
    ),

    if(
      rule!AS_CO_UT_isBlank(ri!bundleFolder),

      null,

      a!localVariables(
        /*Wrapping this in a local variable to ensure caching -- it will only re-execute if inputs change*/
        local!bundleData: loadbundlepropertiesfile(
          ri!bundleFolder,
          local!locale,
          a!flatten(ri!bundleNames)
        ),

        local!bundleData
      )
    )
  )
)

