_a-0000e643-f7f1-8000-9bca-011c48011c48_.sail_code:
/*Clause text and prescription text in rich text plug in*/
a!localVariables(
  local!hideClauseText: true(),
  local!oldValue: substitute(
    striphtml(
      regexreplaceall(
        cons!AS_GCM_TXT_REGEX_FOR_HIDDEN_SPAN_TAG_FILLIN_METADATA,
        ri!oldValue,
        ""
      )
    ),
    char(10),
    ""
  ),
  local!newValue: substitute(
    striphtml(
      regexreplaceall(
        cons!AS_GCM_TXT_REGEX_FOR_HIDDEN_SPAN_TAG_FILLIN_METADATA,
        ri!newValue,
        ""
      )
    ),
    char(10),
    ""
  ),
  {
    #"SYSTEM_SYSRULES_richTextItem_v1"(
      text: {
        #"SYSTEM_SYSRULES_richTextItem_v1"(
          text: {
            "",
            " ",
            rule!AS_GCM_UT_displayFieldNamesForRefClauseAuditHistory(
              fieldName: ri!fieldName,
              bundle: ri!bundle
            ),
            char(32),
            rule!AS_GCM_DSP_showOrHideChangesLink(
              showLessData: local!hideClauseText,
              bundle: ri!bundle,
              showWhen: true()
            )
          }
        ),
        if(
          local!hideClauseText = false(),
          {
            char(13),
            "",
            " ",
            "",
            char(13),
            if(
              rule!AS_CO_UT_isBlank(ri!oldValue),
              #"SYSTEM_SYSRULES_richTextItem_v1"(
                text: "",
                color: "SECONDARY"
              ),
              #"SYSTEM_SYSRULES_richTextItem_v1"(text: local!oldValue)
            ),
            char(13),
            "",
            char(13),
            if(
              rule!AS_CO_UT_isBlank(ri!newValue),
              #"SYSTEM_SYSRULES_richTextItem_v1"(
                text: "",
                color: "SECONDARY"
              ),
              #"SYSTEM_SYSRULES_richTextItem_v1"(text: local!newValue)
            )
          },
          {}
        )
      }
    ),
    rule!AS_GCM_DSP_modificationCommentForFillIns(
      oldValue: ri!oldValue,
      newValue: ri!newValue,
      bundle: ri!bundle,
      showWhen: ri!fieldName = "clauseText"
    )
  }
)

_a-0000e994-0db4-8000-9c52-011c48011c48_.sail_code:
a!localVariables(
  local!triggerRefresh: a!refreshVariable(
    value: now(),
    refreshOnVarChange: ri!triggerRefresh,
    refreshAfter: "RECORD_ACTION"
  ),
  local!clauseSetGenerationDetails: a!refreshVariable(
    value: #"SYSTEM_SYSRULES_queryRecordByIdentifier"(
      recordType: recordType!AS GCM Clause Set,
      identifier: ri!clauseSet[recordType!AS GCM Clause Set.setId],
      fields: {
        recordType!AS GCM Clause Set.completedGenerationSteps,
        recordType!AS GCM Clause Set.refStatusId,
        recordType!AS GCM Clause Set.incompleteClauseCount
      }
    ),
    refreshInterval: if(
      and(
        now() < a!addDateTime(
          startDateTime: a!defaultValue(
            value: ri!clauseSet[recordType!AS GCM Clause Set.modifiedDatetime],
            default: ri!clauseSet[recordType!AS GCM Clause Set.createdDatetime]
          ),
          minutes: cons!AS_GCM_INT_CLAUSE_SET_GRID_AUTO_REFRESH_TIME_OUT_MINUTES
        ),
        or(
          tointeger(
            fv!value[recordType!AS GCM Clause Set.refStatusId]
          ) = cons!AS_GCM_REF_ID_CLAUSE_SET_STATUS_GENERATING,
          ri!isExternalSource
        )
      ),
      0.5,
      null
    ),
    refreshOnVarChange: local!triggerRefresh
  ),
  local!clauseSet: rule!AS_GCM_setRecordFields(
    record: ri!clauseSet,
    alwaysOverwrite: {
      a!map(
        field: recordType!AS GCM Clause Set.incompleteClauseCount,
        value: local!clauseSetGenerationDetails[recordType!AS GCM Clause Set.incompleteClauseCount]
      ),
      a!map(
        field: recordType!AS GCM Clause Set.completedGenerationSteps,
        value: local!clauseSetGenerationDetails[recordType!AS GCM Clause Set.completedGenerationSteps]
      ),
      a!map(
        field: recordType!AS GCM Clause Set.refStatusId,
        value: local!clauseSetGenerationDetails[recordType!AS GCM Clause Set.refStatusId]
      )
    }
  ),
  local!bundle: rule!AS_GCM_CO_UT_loadBundleFromFolder(langISOCode: null),
  local!document,
  local!isDownloadDocument: false(),
  local!clauseSetTemplate: rule!AS_GCM_QE_getClauseSetTemplateDetails(
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT,
    executeWhen: true,
    setId: ri!clauseSetId,
    triggerRefresh: local!triggerRefresh
  ),
  #"SYSTEM_SYSRULES_columnsLayout"(
    marginAbove: "LESS",
    columns: {
      #"SYSTEM_SYSRULES_columnLayout"(
        contents: rule!AS_GCM_CPS_readonlyClauseSetDetails(
          clauseSet: local!clauseSet,
          bundle: local!bundle,
          document: local!document,
          isDownloadDocument: local!isDownloadDocument,
          clauseSetTemplate: local!clauseSetTemplate,
          areActiveClausesAvailable: local!clauseSet[recordType!AS GCM Clause Set.activeClauseCount] > 0
        ),
        width: "NARROW",
        showWhen: rule!AS_CO_UT_not(ri!isExternalSource)
      ),
      #"SYSTEM_SYSRULES_columnLayout"(
        contents: {
          rule!AS_GCM_CPS_readOnlyKeyDetailsSectionInClausesTab(
            bundle: local!bundle,
            clauseSet: local!clauseSet
          ),
          rule!AS_GCM_CPS_appliedClausesSection(
            triggerRefresh: local!triggerRefresh,
            bundle: local!bundle,
            clauseSet: local!clauseSet,
            isExternalSource: ri!isExternalSource,
            relatedActionVisiblity: ri!relatedActionVisibility
          )
        }
      )
    }
  )
)

