/*Using the GAM query entity with selection to exclude "clausesChanges" while querying*/
rule!AS_GAM_UT_queryEntityWithSelection(
  entity: cons!AS_GCM_ENT_A_R_CLAUSE_SET,
  returnType: ri!returnType,
  selectionColumns: if(
    rule!AS_CO_UT_isBlank(ri!excludedColumns),
    null,
    fn!remove(
      rule!AS_CO_UT_returnCdtFields(
        type: 'type!{urn:com:appian:types:AS:GCM}AS_GCM_A_R_ClauseSet',

      ),
      wherecontains(
        ri!excludedColumns,
        rule!AS_CO_UT_returnCdtFields(
          type: 'type!{urn:com:appian:types:AS:GCM}AS_GCM_A_R_ClauseSet',

        )
      )
    )
  ),
  executeWhen: and(
    ri!executeWhen,
    or(
      any(
        rule!AS_CO_UT_isNotBlank(_),
        {
          ri!additionalFilters,
          ri!logicalExpression,
          ri!aggregation
        }
      ),
