_a-0000e447-933d-8000-9baa-011c48011c48_.sail_code:
rule!AS_GCM_QNM_UT_evaluateQuestionPrecedentSet(
  /* There must be at most one top level precedent set per field key */
  questionPrecedentSet: if(
    isnull(
      ri!question.isRequiredPrecedentSets
    ),
    null,
    index(
      index(
        ri!question.isRequiredPrecedentSets,
        wherecontains(
          ri!fieldKey,
          ri!question.isRequiredPrecedentSets.fieldKey
        ),
        {}
      ),
      1,
      null
    )
  ),
  applicableQuestions: ri!applicableQuestions,
  applicableQuestionsMetadata: ri!applicableQuestionsMetadata
)

