_a-0000e9ae-8772-8000-9c5b-011c48011c48_.sail_code:
/*Note: This rule cannot have an assertion as it returns a reaction tree*/
if(
  or(
    ri!clauseSet.refCategory.refDataId = cons!AS_GCM_REF_ID_CATEGORY_AMENDMENT,
    ri!clauseSet.refCategory.refDataId = cons!AS_GCM_REF_ID_CATEGORY_MODIFICATION
  ),
  {
    if(
      ri!isCategory,
      {
        if(
          rule!AS_CO_UT_isBlank(ri!clauseSetContext),
          {
            a!save(ri!clauseSet.isExistingVehicle, null),
            a!save(ri!clauseSet.referencedIdv, null)
          },
          {}
        ),
        a!save(ri!questionnaire, null)
      },
      {}
    ),
    a!save(
      ri!existingClauseSetDetails,
      rule!AS_GCM_updateClauseSetDetailsfromClauseSetContext(
        clauseSet: a!localVariables(
          local!latestFinalizedClauseSetDetails: rule!AS_CO_UT_cast(
            type: 'type!{urn:com:appian:types:AS:GCM}AS_GCM_ClauseSet',
            value: rule!AS_GCM_QE_getClauseSetDetails(
              returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_DATA_SUBSET,
              executeWhen: rule!AS_CO_UT_isNotBlank(ri!clauseSet.setNumber),
              additionalFilters: {
                #"SYSTEM_SYSRULES_queryFilter"(
                  field: "setNumber",
                  operator: "=",
                  value: ri!clauseSet.setNumber
                ),
                #"SYSTEM_SYSRULES_queryFilter"(
                  field: "setId",
                  operator: "<>",
                  value: ri!clauseSet.setId
                ),
                #"SYSTEM_SYSRULES_queryFilter"(
                  field: "refType.refDataId",
                  operator: "=",
                  value: ri!clauseSet.refType.refDataId
                ),
                #"SYSTEM_SYSRULES_queryFilter"(
                  field: "refStatus.refDataId",
                  operator: "=",
                  value: cons!AS_GCM_REF_ID_CLAUSE_SET_STATUS_FINALIZED
                )
              },
              pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
                startIndex: 1,
                batchSize: 1,
                sort: #"SYSTEM_SYSRULES_sortInfo"(field: "validationDate", ascending: false)
              )
            ).data
          ),
          if(
            a!isNullOrEmpty(
              value: local!latestFinalizedClauseSetDetails
            ),
            rule!AS_CO_UT_cast(
              type: 'type!{urn:com:appian:types:AS:GCM}AS_GCM_ClauseSet',
              value: rule!AS_GCM_QE_getClauseSetDetails(
                returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_DATA_SUBSET,
                executeWhen: rule!AS_CO_UT_isNotBlank(ri!clauseSet.setNumber),
                additionalFilters: {
                  #"SYSTEM_SYSRULES_queryFilter"(
                    field: "setNumber",
                    operator: "=",
                    value: ri!clauseSet.setNumber
                  ),
                  #"SYSTEM_SYSRULES_queryFilter"(
                    field: "setId",
                    operator: "<>",
                    value: ri!clauseSet.setId
                  ),
                  #"SYSTEM_SYSRULES_queryFilter"(
                    field: "refType.refDataId",
                    operator: "=",
                    value: ri!clauseSet.refType.refDataId
                  )
                },
                pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
                  startIndex: 1,
                  batchSize: 1,
                  sort: #"SYSTEM_SYSRULES_sortInfo"(field: "setId", ascending: true())
                )
              ).data
            ),
            local!latestFinalizedClauseSetDetails
          )
        ),
        clauseSetContext: ri!clauseSetContext
      )
    ),
    a!save(
      ri!existingContractTypeMaps,
      rule!AS_CO_UT_cast(
        type: 'type!{urn:com:appian:types:AS:GCM}AS_GCM_ClauseSet_ContractType_Map?list',
        value: rule!AS_GCM_QE_getClauseSetContractTypeDetails(
          setId: ri!existingClauseSetDetails.setId,
          executeWhen: a!isNotNullOrEmpty(ri!existingClauseSetDetails.setId)
        )
      )
    ),
    rule!AS_GCM_UT_constructClauseSetBasedOnCategoryAndSetNumber(
      clauseSet: ri!clauseSet,
      isExistingId: ri!isExistingId,
      existingClauseSetDetails: ri!existingClauseSetDetails,
      contractTypeMaps: ri!contractTypeMaps,
      existingContractTypeMaps: ri!existingContractTypeMaps
    )
  },
  {
    a!save(ri!existingContractTypeMaps, null),
    if(
      ri!isCategory,
      {
        rule!AS_GCM_UT_constructClauseSetBasedOnCategoryAndSetNumber(
          clauseSet: ri!clauseSet,
          isExistingId: ri!isExistingId,
          contractTypeMaps: ri!contractTypeMaps,
          existingClauseSetDetails: rule!AS_GCM_updateClauseSetDetailsfromClauseSetContext(clauseSetContext: ri!clauseSetContext),
          existingContractTypeMaps: ri!existingContractTypeMaps
        ),
        if(
          rule!AS_CO_UT_isBlank(ri!clauseSetContext),
          {
            a!save(ri!clauseSet.refContractMethod, null),
            a!save(
              ri!clauseSet.contractPurposeDetail,
              null
            ),
            a!save(ri!clauseSet.contractAmountCheck, null)
          },
          {}
        )
      },
      {}
    ),
    a!save(ri!existingClauseSetDetails, null)
  }
)

