_a-0000eb7c-0389-8000-9d2c-011c48011c48_.sail_code:
a!localVariables(
  local!rowSize: 3,
  local!templateBatchSize: 6,
  local!queriedTemplates: a!refreshVariable(
    value: rule!AS_GCM_QR_getTemplate(
      sort: #"SYSTEM_SYSRULES_sortInfo"(
        field: recordType!AS GCM R Template.templateName,
        ascending: true()
      ),
      getAllRecords: true,
      returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
      executeWhen: true,
      logicalExpression: #"SYSTEM_SYSRULES_queryLogicalExpression"(
        operator: "OR",
        filters: {
          #"SYSTEM_SYSRULES_queryFilter"(
            field: recordType!AS GCM R Template.isActive,
            operator: "=",
            value: true
          ),
          /*Retaining the clause set template if it is made inactive in "Templates" tab*/
          #"SYSTEM_SYSRULES_queryFilter"(
            field: recordType!AS GCM R Template.templateId,
            operator: "=",
            value: rule!AS_CO_UT_indexPath(
              cdt: ri!clauseSetTemplate,
              field: "refTemplate.templateId",
              indexDefault: null
            ),
            applyWhen: a!isNotNullOrEmpty(ri!clauseSetTemplate.refTemplate)
          )
        }
      ),
      fields: {
        recordType!AS GCM R Template.templateId,
        recordType!AS GCM R Template.templateName,
        recordType!AS GCM R Template.templateDescription
      }
    ),
    refreshOnReferencedVarChange: false
  ),
  local!templatesCount: count(local!queriedTemplates),
  local!selectedTemplateIndex: wherecontains(
    tointeger(
      rule!AS_CO_UT_indexPath(
        cdt: ri!clauseSetTemplate,
        field: "refTemplate.templateId",
        indexDefault: null
      )
    ),
    tointeger(
      local!queriedTemplates[recordType!AS GCM R Template.templateId]
    )
  ),
  local!startIndex: a!refreshVariable(
    value: if(
      a!isNotNullOrEmpty(
        rule!AS_CO_UT_indexPath(
          cdt: ri!clauseSetTemplate,
          field: "refTemplate.templateId",
          indexDefault: null
        )
      ),
      /* Calculating the highest mulitple of the batch size for local!selectedTemplateIndex
      and adding 1 to determine the start index */
      tointeger(
        floor(
          local!selectedTemplateIndex - 1,
          local!templateBatchSize
        ) + 1
      ),
      1
    ),
    refreshOnReferencedVarChange: false
  ),
  /*Filtering the indexes that are to be displayed*/
  local!filteredTemplateIndexes: intersection(
    enumerate(local!templateBatchSize) + local!startIndex,
    enumerate(local!templatesCount) + 1
  ),
  /*Applying Pagination*/
  local!templates: index(
    local!queriedTemplates,
    local!filteredTemplateIndexes,
    null
  ),
  local!isExpanded: true,
  local!selectedTemplate: a!refreshVariable(
    value: a!defaultValue(
      value: rule!AS_CO_UT_cast(
        type: recordType!AS GCM R Template,
        value: ri!clauseSetTemplate.refTemplate
      ),
      default: recordType!AS GCM R Template()
    ),
    refreshOnReferencedVarChange: false
  ),
  rule!AS_GCM_CRD_collapsibleSection(
    bundle: ri!bundle,
    sectionLabel: "",
    isExpanded: local!isExpanded,
    showWhen: ri!showWhen,
    contents: #"SYSTEM_SYSRULES_cardLayout"(
      contents: {
        if(
          a!isNullOrEmpty(local!queriedTemplates),
          {
            #"SYSTEM_SYSRULES_richTextDisplayField"(
              labelPosition: "COLLAPSED",
              value: {
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: "",
                  color: "SECONDARY"
                )
              },
              align: "CENTER"
            )
          },
          {
            #"SYSTEM_SYSRULES_richTextDisplayField"(
              labelPosition: "COLLAPSED",
              value: {
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: "Choose a template that is applicable to this clause set.",
                  color: "SECONDARY"
                )
              }
            ),
            #"SYSTEM_SYSRULES_forEach"(
              items: fn!enumerate(local!templateBatchSize / local!rowSize),
              expression: {
                a!localVariables(
                  local!row: fv!index,
                  #"SYSTEM_SYSRULES_columnsLayout"(
                    columns: #"SYSTEM_SYSRULES_forEach"(
                      items: fn!enumerate(local!rowSize),
                      expression: a!localVariables(
                        local!templateIndex: (local!row - 1) * local!rowSize + fv!index,
                        local!template: if(
                          local!templateIndex > length(local!templates),
                          {},
                          index(local!templates, local!templateIndex)
                        ),
                        {
                          #"SYSTEM_SYSRULES_columnLayout"(
                            contents: {
                              if(
                                a!isNullOrEmpty(local!template),
                                {},
                                /*Template Cards*/
                                #"SYSTEM_SYSRULES_cardLayout"(
                                  contents: rule!AS_GCM_CRD_templateDetails(
                                    templateName: local!template[recordType!AS GCM R Template.templateName],
                                    templateDescription: local!template[recordType!AS GCM R Template.templateDescription],
                                    selected: tointeger(
                                      local!selectedTemplate[recordType!AS GCM R Template.templateId]
                                    ) = tointeger(
                                      local!template[recordType!AS GCM R Template.templateId]
                                    ),
                                    bundle: ri!bundle
                                  ),
                                  link: #"SYSTEM_SYSRULES_dynamicLink"(
                                    label: if(
                                      cons!AS_GCM_TOGGLE_AUTOMATION_TESTING_ENABLED,
                                      local!template[recordType!AS GCM R Template.templateName],
                                      ""
                                    ),
                                    saveInto: {
                                      a!match(
                                        value: local!template[recordType!AS GCM R Template.templateId],
                                        whenTrue: tointeger(
                                          local!selectedTemplate[recordType!AS GCM R Template.templateId]
                                        ) = fv!value,
                                        then: a!save(
                                          local!selectedTemplate,
                                          recordType!AS GCM R Template()
                                        ),
                                        default: a!save(
                                          target: local!selectedTemplate,
                                          value: rule!AS_CO_UT_cast(
                                            type: recordType!AS GCM R Template,
                                            value: local!template
                                          )
                                        )
                                      ),
                                      a!save(
                                        ri!clauseSetTemplate.refTemplate,
                                        if(
                                          rule!AS_CO_UT_isBlank(
                                            local!selectedTemplate[recordType!AS GCM R Template.templateId]
                                          ),
                                          null,
                                          local!selectedTemplate
                                        )
                                      ),
                                      a!save(
                                        ri!clauseSetTemplate,
                                        rule!AS_CO_UT_captureMetadata(
                                          cdt: rule!AS_CO_UT_cast(
                                            type: 'type!{urn:com:appian:types:AS:GCM}AS_GCM_ClauseSet_Template',
                                            value: ri!clauseSetTemplate
                                          ),
                                          modifiedBy: loggedInUser()
                                        )
                                      ),
                                      /*Saving Template Clauses*/
                                      a!save(
                                        ri!templateClauses,
                                        rule!AS_GCM_QR_getTemplateClauseDetails(
                                          returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
                                          executeWhen: a!isNotNullOrEmpty(
                                            local!selectedTemplate[recordType!AS GCM R Template.templateId]
                                          ),
                                          templateIds: local!selectedTemplate[recordType!AS GCM R Template.templateId],
                                          showInActive: false(),
                                          fields: {
                                            recordType!AS GCM Template Clause.relationId,
                                            recordType!AS GCM Template Clause.refClause.clauseId,
                                            recordType!AS GCM Template Clause.refClause.refType.refDataId,
                                            recordType!AS GCM Template Clause.refUsage.refDataId,
                                            recordType!AS GCM Template Clause.refClause.clauseTitle,
                                            recordType!AS GCM Template Clause.refClause.effectiveDate,
                                            recordType!AS GCM Template Clause.isActive
                                          }
                                        )
                                      )
                                    }
                                  ),
                                  shape: "SEMI_ROUNDED",
                                  padding: "NONE",
                                  showBorder: false()
                                )
                              )
                            }
                          )
                        }
                      )
                    ),
                    marginBelow: "STANDARD",
                    spacing: "DENSE"
                  )
                )
              }
            ),
            rule!AS_GCM_CP_customPagingIcons(
              startIndex: local!startIndex,
              batchSize: local!templateBatchSize,
              totalCount: local!templatesCount,
              bundle: ri!bundle
            )
          }
        )
      },
      height: "AUTO",
      style: "NONE",
      shape: "SEMI_ROUNDED",
      padding: "MORE",
      marginBelow: "STANDARD",
      showBorder: false
    )
  )
)

