cast(
  'type!{http://www.appian.com/ae/types/2009}Text',
  a!localVariables(
    local!length: len(ri!value),
    local!minLength: rule!AS_CO_UT_replaceNull(
      nullableValue: ri!minLength,
      replacementValue: 0
    ),
    if(
      /* Does not meet exact length requirement */
      and(
        rule!AS_CO_UT_isNotBlank(ri!minLength),
        rule!AS_CO_UT_isNotBlank(ri!maxLength),
        ri!minLength = ri!maxLength,
        local!length <> ri!minLength
      ),
      rule!AS_CO_UT_replaceNull(
        nullableValue: rule!AS_CO_UT_replaceNull(
          nullableValue: ri!maxLengthMessage,
          replacementValue: ri!minLengthMessage
        ),
        replacementValue: rule!AS_CO_UT_displayCommonObjectBundleLabel(
          bundleKey: if(
            ri!minLength = 1,
            "AS.CO.CommonObjects.vld_EnterExactlySingle",
            "AS.CO.CommonObjects.vld_EnterExactlyMultiple"
          ),
          arguments: ri!minLength
        )
      ),
      /*Too short*/
      if(
        local!length < ri!minLength,
        rule!AS_CO_UT_replaceNull(
          nullableValue: ri!minLengthMessage,
          replacementValue: rule!AS_CO_UT_displayCommonObjectBundleLabel(
            bundleKey: "AS.CO.CommonObjects.vld_EnterAtLeast",
            arguments: local!minLength
          )
        ),
        /*Too long*/
        if(
          and(
            rule!AS_CO_UT_isNotBlank(ri!maxLength),
            local!length > ri!maxLength
          ),
          rule!AS_CO_UT_replaceNull(
            nullableValue: ri!maxLengthMessage,
            replacementValue: rule!AS_CO_UT_displayCommonObjectBundleLabel(
              bundleKey: if(
                ri!maxLength = 1,
                "AS.CO.CommonObjects.vld_EnterNoMoreThanSingle",
                "AS.CO.CommonObjects.vld_EnterNoMoreThanMultiple"
              ),
              arguments: ri!maxLength
            )
          ),
          /*Valid*/
          ""
        )
      )
    )
  )
)

_a-0000ed08-d796-8000-9dda-011c48011c48_.sail_code:
with(
  local!newIndices: a!match(
    value: null,
    /*Null groups and documents don't filter properly, adding logic to support that*/
    whenTrue: and(
      a!isNullOrEmpty(ri!fieldValuePair.value),
      runtimetypeof(ri!records[ri!fieldValuePair.field]) = 'type!{http://www.appian.com/ae/types/2009}Group?list'
    ),
    then: wherecontains(
      cast(
        'type!{http://www.appian.com/ae/types/2009}Group',
        null
      ),
      ri!records[ri!fieldValuePair.field]
    ),
    whenTrue: and(
      a!isNullOrEmpty(ri!fieldValuePair.value),
      runtimetypeof(ri!records[ri!fieldValuePair.field]) = 'type!{http://www.appian.com/ae/types/2009}Document?list'
    ),
    then: wherecontains(
      cast(
        'type!{http://www.appian.com/ae/types/2009}Document',
        null
      ),
      ri!records[ri!fieldValuePair.field]
    ),
    whenTrue: ri!matchType = "CONTAINS",
    then: where(
      apply(
        rule!AS_CO_UT_ContainsValueByField(
          record: _,
          field: ri!fieldValuePair.field,
          value: touniformstring(ri!fieldValuePair.value)
        ),
        ri!records
      )
    ),
    whenTrue: ri!matchType = "EXACT",
    /*Standard filter logic*/
    then: wherecontains(
      touniformstring(ri!fieldValuePair.value),
      touniformstring(ri!records[ri!fieldValuePair.field])
    ),
    default: fn!error(
      "Invalid matchType, must be one of: EXACT, CONTAINS"
    )
  ),
  if(
    ri!matchingCriteria = "ANY",
    union(ri!previousIndices, local!newIndices),
    intersection(ri!previousIndices, local!newIndices)
  )
)

