_a-0000e897-ddd7-8000-9c0c-011c48011c48_.sail_code:
rule!AS_CO_UT_queryRecord(
  recordType: recordType!AS GCM Template Clause,
  returnType: a!defaultValue(
    value: ri!returnType,
    default: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY
  ),
  executeWhen: and(
    ri!executeWhen,
    or(
      any(
        rule!AS_CO_UT_isNotBlank(_),
        {
          ri!additionalFilters,
          ri!logicalExpression,
          ri!aggregation
        }
      ),
      rule!AS_CO_UT_isNotBlank(ri!templateIds),
      rule!AS_CO_UT_isNotBlank(ri!relationIds)
    )
  ),
  /* To ensure proper null checks, we'll force all queryFilter values to not be null */
  logicalExpression: ri!logicalExpression,
  pagingInfo: ri!pagingInfo,
  sort: ri!sort,
  triggerRefresh: ri!triggerRefresh,
  fields: rule!AS_CO_UT_rejectNullValuesFromArray(
    {
      ri!fields,
      if(
        ri!includeRelatedRecords,
        {
          recordType!AS GCM Template Clause.refClause,
          recordType!AS GCM Template Clause.refTemplate,
          recordType!AS GCM Template Clause.refUsage
        },
        {}
      )
    }
  ),
  filters: {
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!AS GCM Template Clause.relationId,
      operator: "in",
      value: ri!relationIds
    ),
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!AS GCM Template Clause.templateId,
      operator: "in",
      value: ri!templateIds
    ),
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!AS GCM Template Clause.isActive,
      operator: "=",
      value: true(),
      applyWhen: ri!showInActive <> true()
    ),
    ri!additionalFilters
  }
)

_a-0000e447-933d-8000-9baa-011c48011c48_.sail_code:
/* A null question precedent set is always considered fulfilled */
or(
  isnull(ri!questionPrecedentSet),
  and(
    /* A non-null precedent set with no precedents or precedent sets can intentionally never be fulfilled */
    or(
      length(
        {
          ri!questionPrecedentSet.questionPrecedentSets
        }
      ) <> 0,
      length(
        {
          ri!questionPrecedentSet.questionPrecedents
        }
      ) <> 0
    ),
    with(
      /* Below local variables should only reevaluate on changes to the applicable questions as defined in rules calling this rule */
      local!questionPrecedentSetsResults: apply(
        rule!AS_GCM_QNM_UT_evaluateQuestionPrecedentSet(
          questionPrecedentSet: _,
          applicableQuestions: ri!applicableQuestions,
          applicableQuestionsMetadata: ri!applicableQuestionsMetadata
        ),
        ri!questionPrecedentSet.questionPrecedentSets
      ),
      local!questionPrecedentsResults: apply(
        rule!AS_GCM_QNM_UT_evaluateQuestionPrecedent(
          questionPrecedent: _,
          question_precedent: _,
          question_precedentMetadata: _
        ),
        merge(
          /* Merge only supports list arguments, so we need to substitute an empty list when null */
          if(
            isnull(
              ri!questionPrecedentSet.questionPrecedents
            ),
            {},
            ri!questionPrecedentSet.questionPrecedents
          ),
          index(
            ri!applicableQuestions,
            apply(
              wherecontains(_, ri!applicableQuestions.questionId),
              index(
                ri!questionPrecedentSet.questionPrecedents,
                "questionId_precedent",
                {}
              )
            ),
            null
          ),
          index(
            ri!applicableQuestionsMetadata,
            apply(
              wherecontains(
                _,
                ri!applicableQuestionsMetadata.questionId
              ),
              index(
                ri!questionPrecedentSet.questionPrecedents,
                "questionId_precedent",
                {}
              )
            ),
            null
          )
        )
      ),
      local!allResults: {
        local!questionPrecedentSetsResults,
        local!questionPrecedentsResults
      },
      local!evaluation: displayvalue(
        ri!questionPrecedentSet.operator,
        {
          cons!AS_GCM_QNM_ENUM_PRECEDENT_OPERATOR_AND,
          cons!AS_GCM_QNM_ENUM_PRECEDENT_OPERATOR_OR,
          cons!AS_GCM_QNM_ENUM_PRECEDENT_OPERATOR_NOT,

        },
        {
          and(local!allResults),
          or(local!allResults),
          not(or(local!allResults))
        },
        null
      ),
      if(
        isnull(local!evaluation),
        error(
          concat(
            {
              "Question Precedent Set (ID: ",
              ri!questionPrecedentSet.questionPrecedentSetId,
              ") does not have a valid operator (operator: ",
              ri!questionPrecedentSet.operator,
              ").  See this rule for a list of valid operators."
            }
          )
        ),
        local!evaluation
      )
    )
  )
)

