                              componentsMap: local!contractDetailsComponentsMap
                            )
                          },
                          /*Answer Questionnaire*/
                          {
                            rule!AS_GCM_CPS_answerQuestionnaireSection(
                              clauseSet: local!varMap.variable.clauseSet,
                              questionnaire: ri!questionnaire,
                              bundle: local!varMap.variable.bundle
                            )
                          },
                          {}
                        )
                      }
                    )
                  }
                ),
                #"SYSTEM_SYSRULES_horizontalLine"(),
                #"SYSTEM_SYSRULES_columnsLayout"(
                  columns: {
                    #"SYSTEM_SYSRULES_columnLayout"(
                      contents: {
                        #"SYSTEM_SYSRULES_ButtonArrayLayout"(
                          buttons: {
                            #"SYSTEM_SYSRULES_buttonWidget_v1"(
                              label: "Back",
                              value: false(),
                              saveInto: {
                                ri!userAction,
                                a!save(
                                  local!varMap.variable.activeStep,
                                  local!varMap.variable.activeStep - 1
                                )
                              },
                              submit: false,
                              style: "OUTLINE",
                              showWhen: local!varMap.variable.activeStep <> 1,
                              validate: false
                            ),
                            #"SYSTEM_SYSRULES_buttonWidget_v1"(
                              label: "Cancel",
                              value: cons!AS_CO_ENUM_USER_ACTION_CANCEL,
                              saveInto: { ri!userAction },
                              submit: true,
                              style: "LINK",
                              validate: false
                            )
                          },
                          align: "START",
                          marginBelow: "NONE"
                        )
                      }
                    ),
                    #"SYSTEM_SYSRULES_columnLayout"(contents: {}),
                    #"SYSTEM_SYSRULES_columnLayout"(
                      contents: {
                        #"SYSTEM_SYSRULES_ButtonArrayLayout"(
                          buttons: {
                            #"SYSTEM_SYSRULES_buttonWidget_v1"(
                              label: if(
                                a!isNullOrEmpty(ri!clauseSet.setId),
                                "",
                                "Update"
                              ),
                              value: cons!AS_CO_ENUM_USER_ACTION_SUBMIT,
                              saveInto: {
                                ri!userAction,
                                /*Saving Questionnaire*/
                                a!save(
                                  ri!questionnaire.questions,
                                  rule!AS_GCM_QNM_UT_clearHiddenResponses(
                                    allQuestions: ri!questionnaire.questions
                                  )
                                ),
                                if(
                                  rule!AS_GCM_doesQuestionnaireContainResponses(questionnaire: ri!questionnaire),
                                  a!save(ri!questionnaire, ri!questionnaire),
                                  a!save(ri!questionnaire, null)
                                ),
                                /*Nullifying the questionnaire / updating the questionIds as null*/
                                if(
                                  or(
                                    a!isNotNullOrEmpty(ri!questionnaire.questionnaireId),
                                    rule!AS_CO_UT_isBlank(ri!questionnaire)
                                  ),
                                  {},
                                  {
                                    rule!AS_GCM_UT_replaceQuestionIdWithNullForQuestionnaire(
                                      questionnaire: ri!questionnaire,
                                      existingQuestionnaire: local!existingQuestionnaire
                                    ),
                                    a!save(
                                      ri!questionnaire.questionCategories.questionCategoryId,
                                      null
                                    )
                                  }
                                ),
                                if(
                                  tointeger(
                                    index(ri!questionnaire, "questionnaireId", {})
                                  ) <> tointeger(
                                    index(
                                      local!existingQuestionnaire,
                                      "questionnaireId",
                                      {}
                                    )
                                  ),
                                  a!save(
                                    ri!deletedQuestionnaireId,
                                    index(
                                      local!existingQuestionnaire,
                                      "questionnaireId",
                                      {}
                                    )
                                  ),
                                  {}
                                ),
                                a!save(
                                  ri!clauseSetTemplate,
                                  local!varMap.variable.clauseSetTemplate
                                ),
                                a!save(
                                  ri!originalClauseSetTemplate,
                                  local!varMap.variable.originalClauseSetTemplate
                                ),
                                /*Saving Contract types*/
                                a!save(
                                  ri!contractTypeMaps,
                                  rule!AS_GCM_BL_getUpdatedContractTypeMaps(
                                    clauseSetContractTypes: local!varMap.variable.contractTypeMaps,
                                    originalClauseSetContractTypes: local!varMap.variable.originalClauseSetContractTypes
                                  )
                                ),
                                a!save(
                                  ri!originalContractTypeMaps,
                                  local!varMap.variable.originalClauseSetContractTypes
                                ),
                                a!save(
                                  ri!clauseSet,
                                  local!varMap.variable.clauseSet
                                ),
                                a!save(
                                  ri!originalClauseSet,
                                  local!varMap.variable.originalClauseSet
                                ),
                                /*App metrics*/
                                rule!AS_GCM_UT_saveIntoForAppMetricsInClauseSet(
                                  countOfQuestions: local!varMap.variable.countOfQuestions,
                                  questionnaire: ri!questionnaire
                                ),
                                if(
                                  a!isNotNullOrEmpty(ri!clauseSet.setId),
                                  /*App metrics for updateClauseset*/
                                  rule!AS_GCM_MTR_SAVE_updateClauseSet(),
                                  /*Metric for number of new clauseSet created*/
                                  rule!AS_GCM_MTR_SAVE_createClauseSet()
                                ),
                                a!save(
                                  ri!originalQuestionnaire,
                                  local!existingQuestionnaire
                                )
                              },
                              submit: true,
                              style: "SOLID",
                              showWhen: local!varMap.variable.activeStep = 2,
                              validate: true,
                              confirmHeader: if(
                                a!isNotNullOrEmpty(ri!clauseSet.setId),
                                "",
                                {}
                              ),
                              confirmMessage: if(
                                a!isNotNullOrEmpty(ri!clauseSet.setId),
                                "",
                                {}
                              ),
                              confirmButtonLabel: if(
                                a!isNotNullOrEmpty(ri!clauseSet.setId),
                                "",
                                {}
                              ),
                              cancelButtonLabel: if(
                                a!isNotNullOrEmpty(ri!clauseSet.setId),
                                "",
                                {}
                              ),
                              disabled: and(
                                rule!AS_CO_UT_isNotBlank(local!defaultQuestionnaire),
                                rule!AS_GCM_QNM_UT_calculateCompletionPercentage(
                                  calculatedQuestionMetadata: rule!AS_GCM_QNM_UT_calculateQuestionMetadata(
                                    allQuestions: index(ri!questionnaire, "questions", {})
                                  )
                                ) <> 100
                              )
                            ),
                            #"SYSTEM_SYSRULES_buttonWidget_v1"(
                              label: "Next",
                              value: cons!AS_CO_ENUM_USER_ACTION_NEXT,
                              saveInto: {
                                ri!userAction,
                                if(
                                  a!isNotNullOrEmpty(
                                    local!contractDetailsFieldsWithValidationErrors
                                  ),
                                  a!save(local!contractDetailsExpanded, true),
                                  /*Saving Questionnaire*/
                                  {
                                    if(
                                      rule!AS_CO_UT_isBlank(ri!questionnaire),
                                      if(
                                        rule!AS_CO_UT_isBlank(local!defaultQuestionnaire),
                                        a!save(ri!questionnaire, null),
                                        {
                                          a!save(
                                            ri!questionnaire,
                                            rule!AS_GCM_QNM_UT_buildQuestionnaire_RuntimeFromTemplate(
                                              questionnaire_Template: local!defaultQuestionnaire,
                                              isPreview: true
                                            )
                                          ),
                                          if(
                                            tointeger(
                                              index(ri!questionnaire, "questionnaireId", {})
                                            ) <> tointeger(
                                              index(
                                                local!existingQuestionnaire,
                                                "questionnaireId",
                                                {}
                                              )
                                            ),
                                            a!save(
                                              ri!deletedQuestionnaireId,
                                              index(
                                                local!existingQuestionnaire,
                                                "questionnaireId",
                                                {}
                                              )
                                            ),
                                            {}
                                          )
                                        }
                                      ),
                                      {}
                                    ),
                                    a!save(
                                      local!varMap.variable.activeStep,
                                      local!varMap.variable.activeStep + 1
                                    )
                                  }
                                )
                              },
                              submit: false,
                              style: "SOLID",
                              showWhen: local!varMap.variable.activeStep = 1,
                              validate: true
                            ),

                          },
                          showWhen: true(),
                          align: "END"
                        )
                      }
                    )
                  },

                )
              },
              width: "WIDE_PLUS"
            ),
            #"SYSTEM_SYSRULES_columnLayout"(contents: {})
          }
        )
      },
      backgroundColor: cons!AS_GCM_HEX_CODE_APPIAN_BACKGROUND
    )
  )
)

_a-0000e9ae-8772-8000-9c5b-011c48011c48_.sail_code:
a!localVariables(
  local!clauseSetContext: rule!AS_GCM_DefaultClauseSetContextFields(
    clauseSetContext: ri!clauseSetContext
  ),
  /*The exact below keys should be passed from GCW*/
  local!refDataFieldsMap: rule!AS_GAM_createMap(
    /*Represents the key in the context map that is passed from GCW*/
    keys: {
      "refTypeId",
      "refCategoryId",
      "refContractMethodId",
      "refContractFormatId"
    },
    /*Represents the fields in GCA*/
    values: {
      "refType",
      "refCategory",
      "refContractMethod",
      "refContractFormat"
    }
  ),
  local!refData: rule!AS_GCM_QR_getRefData(
    refDataId: property(
      local!clauseSetContext,
      a!keys(local!refDataFieldsMap)
    )
  ),
  rule!AS_CO_UT_updateCdtFieldsByFieldValuePairs(
    cdt: ri!clauseSet,
    fieldValuePairs: {
      /*Updating only simple fields*/
      #"SYSTEM_SYSRULES_forEach"(
        items: intersection(
          a!keys(local!clauseSetContext),
          a!keys(
            'type!{urn:com:appian:types:AS:GCM}AS_GCM_ClauseSet'()
          )
        ),
        expression: {
          field: fv!item,
          value: rule!AS_CO_UT_cast(
            typeOf: index(ri!clauseSet, fv!item, null()),
            value: a!defaultValue(
              value: property(local!clauseSetContext, fv!item, null()),
              default: index(ri!clauseSet, fv!item, null())
            )
          )
        }
      ),
      /*Updating the refData fields*/
      #"SYSTEM_SYSRULES_forEach"(
        items: intersection(
          a!keys(local!clauseSetContext),
          a!keys(local!refDataFieldsMap)
        ),
        expression: {
          field: local!refDataFieldsMap[fv!item],
          value: if(
            a!isNullOrEmpty(property(local!clauseSetContext, fv!item)),
            index(
              ri!clauseSet,
              local!refDataFieldsMap[fv!item],
              null()
            ),
            rule!AS_CO_UT_filterCdtByField(
              cdt: local!refData,
              field: "refDataId",
              value: tointeger(property(local!clauseSetContext, fv!item))
            )
          )
        }
      )
    }
  )
)

_a-0000e9a5-4d17-8000-9c58-011c48011c48_.sail_code:
a!localVariables(
  /*Query latest finalized version of Solicitation/Award ID*/
  local!latestFinalizedClauseSetDetails: rule!AS_CO_UT_cast(
    type: 'type!{urn:com:appian:types:AS:GCM}AS_GCM_ClauseSet',
    value: rule!AS_GCM_QE_getClauseSetDetails(
      returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_DATA_SUBSET,
      executeWhen: rule!AS_CO_UT_isNotBlank(ri!clauseSet.setNumber),
      additionalFilters: {
        #"SYSTEM_SYSRULES_queryFilter"(
          field: "setNumber",
          operator: "=",
          value: ri!clauseSet.setNumber
        ),
        #"SYSTEM_SYSRULES_queryFilter"(
          field: "setId",
          operator: "<>",
          value: ri!clauseSet.setId
        ),
        #"SYSTEM_SYSRULES_queryFilter"(
          field: "refType.refDataId",
          operator: "=",
          value: ri!clauseSet.refType.refDataId
        ),
        #"SYSTEM_SYSRULES_queryFilter"(
          field: "refStatus.refDataId",
          operator: "=",
          value: cons!AS_GCM_REF_ID_CLAUSE_SET_STATUS_FINALIZED
        )
      },
      pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
        startIndex: 1,
        batchSize: 1,
        sort: #"SYSTEM_SYSRULES_sortInfo"(field: "validationDate", ascending: false)
      )
    ).data
  ),
  /*If there is no finalized clause set for the set number query the original clause set*/
  local!existingClauseSetDetails: rule!AS_GCM_updateClauseSetDetailsfromClauseSetContext(
    clauseSet: if(
      a!isNullOrEmpty(
        value: local!latestFinalizedClauseSetDetails
      ),
      rule!AS_CO_UT_cast(
        type: 'type!{urn:com:appian:types:AS:GCM}AS_GCM_ClauseSet',
        value: rule!AS_GCM_QE_getClauseSetDetails(
          returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_DATA_SUBSET,
          executeWhen: rule!AS_CO_UT_isNotBlank(ri!clauseSet.setNumber),
          additionalFilters: {
            #"SYSTEM_SYSRULES_queryFilter"(
              field: "setNumber",
              operator: "=",
              value: ri!clauseSet.setNumber
            ),
            #"SYSTEM_SYSRULES_queryFilter"(
              field: "setId",
              operator: "<>",
              value: ri!clauseSet.setId
            ),
            #"SYSTEM_SYSRULES_queryFilter"(
              field: "refType.refDataId",
              operator: "=",
              value: ri!clauseSet.refType.refDataId
            )
          },
          pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
            startIndex: 1,
            batchSize: 1,
            sort: #"SYSTEM_SYSRULES_sortInfo"(field: "setId", ascending: true())
          )
        ).data
      ),
      local!latestFinalizedClauseSetDetails
    ),
    clauseSetContext: ri!clauseSetContext
  ),
  local!clauseSetAssigned: if(
    or(
      tointeger(
        index(ri!clauseSetContext, "refCategoryId", {})
      ) = cons!AS_GCM_REF_ID_CATEGORY_MODIFICATION,
      tointeger(
        index(ri!clauseSetContext, "refCategoryId", {})
      ) = cons!AS_GCM_REF_ID_CATEGORY_AMENDMENT
    ),
    rule!AS_CO_UT_updateCdtFieldsByFieldValuePairs(
      cdt: ri!clauseSet,
      fieldValuePairs: {
        {
          field: "contractAmountCheck",
          value: index(
            local!existingClauseSetDetails,
            "contractAmountCheck",
            ""
          )
        },
        {
          field: "contractPurposeDetail",
          value: index(
            local!existingClauseSetDetails,
            "contractPurposeDetail",
            ""
          )
        },
        {
          field: "refContractMethod",
          value: index(
            local!existingClauseSetDetails,
            "refContractMethod",
            ""
          )
        },
        {
          field: "contractStartDate",
          value: index(
            local!existingClauseSetDetails,
            "contractStartDate",
            ""
          )
        },
        {
          field: "contractEndDate",
          value: index(
            local!existingClauseSetDetails,
            "contractEndDate",
            ""
          )
        },
        {
          field: "estimatedDollarAmt",
          value: index(
            local!existingClauseSetDetails,
            "estimatedDollarAmt",
            ""
          )
        },
        {
          field: "pscCode",
          value: index(
            local!existingClauseSetDetails,
            "pscCode",
            ""
          )
        },
        {
          field: "naicsCode",
          value: index(
            local!existingClauseSetDetails,
            "naicsCode",
            ""
          )
        }
      }
    ),
    ri!clauseSet
  ),
  local!clauseSet: local!clauseSetAssigned,
  /*Querying the existing contract type maps*/
  local!existingContractTypeMaps: rule!AS_CO_UT_cast(
    type: 'type!{urn:com:appian:types:AS:GCM}AS_GCM_ClauseSet_ContractType_Map?list',
    value: rule!AS_GCM_QE_getClauseSetContractTypeDetails(
      setId: local!existingClauseSetDetails.setId,
      executeWhen: rule!AS_CO_UT_isNotBlank(local!existingClauseSetDetails.setId)
    )
  ),
  local!contractTypeMapsAssigned: if(
    or(
      tointeger(
        index(ri!clauseSetContext, "refCategoryId", {})
      ) = cons!AS_GCM_REF_ID_CATEGORY_MODIFICATION,
      tointeger(
        index(ri!clauseSetContext, "refCategoryId", {})
      ) = cons!AS_GCM_REF_ID_CATEGORY_AMENDMENT
    ),
    #"SYSTEM_SYSRULES_forEach"(
      items: local!existingContractTypeMaps,
      expression: rule!AS_CO_UT_updateCdtFields(
        cdt: fv!item,
        fields: "mappingId",
        newValues: if(
          fv!item.setId = ri!clauseSet.setId,
          fv!item.mappingId,
          null
        )
      )
    ),
    ri!contractTypeMaps
  ),
  local!contractTypeMaps: local!contractTypeMapsAssigned,
  /*Making a copy of the original contract type maps*/
  local!originalClauseSetContractTypes: a!refreshVariable(
    value: rule!AS_GCM_QE_getClauseSetContractTypeDetails(
      returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
      setId: ri!clauseSet.setId,
      executeWhen: a!isNotNullOrEmpty(ri!clauseSet.setId)
    ),
    refreshOnReferencedVarChange: false()
  ),
  local!clauseSetTemplate: rule!AS_GCM_QE_getClauseSetTemplateDetails(
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT,
    executeWhen: rule!AS_CO_UT_isNotBlank(ri!clauseSet.setId),
    setId: ri!clauseSet.setId
  ),
  local!originalClauseSetTemplate: a!refreshVariable(
    value: local!clauseSetTemplate,
    refreshOnReferencedVarChange: false()
  ),
  local!originalClauseSet: if(
    a!isNotNullOrEmpty(ri!clauseSet.setId),
    ri!clauseSet,
    'type!{urn:com:appian:types:AS:GCM}AS_GCM_ClauseSet'()
  ),
  local!refData: rule!AS_GCM_QR_getRefData(
    refTypes: {
      cons!AS_GCM_REF_TYPE_CLAUSE_SET_TYPE,
      cons!AS_GCM_REF_TYPE_CONTRACT_PURPOSE,
      cons!AS_GCM_REF_TYPE_CONTRACT_PURPOSE_TYPE_COMMERCIAL,
      cons!AS_GCM_REF_TYPE_CONTRACT_PURPOSE_TYPE_NON_COMMERCIAL,
      cons!AS_GCM_REF_TYPE_PRICING_TYPE,
      cons!AS_GCM_REF_TYPE_CONTRACT_METHOD,
      cons!AS_GCM_REF_TYPE_SOLICITATION_TYPE,
      cons!AS_GCM_REF_TYPE_AWARD_TYPE,
      cons!AS_GCM_REF_TYPE_CLAUSE_ADDED_VIA,
      cons!AS_GCM_REF_TYPE_CLAUSE_SET_CLAUSE_STATUS,
      cons!AS_GCM_REF_TYPE_USAGE,
      cons!AS_GCM_REF_TYPE_CLAUSE_TYPE,
      cons!AS_GCM_REF_TYPE_CLAUSE_SOURCE,
      cons!AS_GCM_REF_TYPE_CONTRACT_FORMAT
    }
  ),
  /*Returns a map with multiple properties*/
  a!map(
    /*field contains all the data used in fields*/
    field: a!map(
      /* Field - Type*/
      type: index(
        local!refData,
        rule!AS_CO_UT_whereContains(
          values: cons!AS_GCM_REF_TYPE_CLAUSE_SET_TYPE,
          array: local!refData.refType
        ),
        {}
      ),
      /* Field - Category*/
      category: a!map(
        award: index(
          local!refData,
          rule!AS_CO_UT_whereContains(
            values: cons!AS_GCM_REF_TYPE_AWARD_TYPE,
            array: local!refData.refType
          ),
          {}
        ),
        solicitation: index(
          local!refData,
          rule!AS_CO_UT_whereContains(
            values: cons!AS_GCM_REF_TYPE_SOLICITATION_TYPE,
            array: local!refData.refType
          ),
          {}
        )
      ),
      /* Field - Contract Method*/
      contractMethod: index(
        local!refData,
        rule!AS_CO_UT_whereContains(
          values: cons!AS_GCM_REF_TYPE_CONTRACT_METHOD,
          array: local!refData.refType
        ),
        {}
      ),
      contractFormat: index(
        local!refData,
        rule!AS_CO_UT_whereContains(
          values: cons!AS_GCM_REF_TYPE_CONTRACT_FORMAT,
          array: local!refData.refType
        ),
        {}
      ),

    ),
    /*Contains refData*/
    refData: a!map(
      /*Contains all refData*/
      all: local!refData,
      /*refData separated by refType*/
      refType: a!map(
        addedVia: index(
          local!refData,
          rule!AS_CO_UT_whereContains(
            values: cons!AS_GCM_REF_TYPE_CLAUSE_ADDED_VIA,
            array: local!refData.refType
          ),
          {}
        ),
        usage: index(
          local!refData,
          rule!AS_CO_UT_whereContains(
            values: cons!AS_GCM_REF_TYPE_USAGE,
            array: local!refData.refType
          ),
          {}
        )
      )
    ),
    /*Contains all variables*/
    variable: a!map(
      activeStep: 1,
      totalSteps: 2,
      bundle: rule!AS_GCM_CO_UT_loadBundleFromFolder(langISOCode: null()),
      countOfQuestions: null(),
      isExistingId: false,
      clauseSet: local!clauseSet,
      originalClauseSet: local!originalClauseSet,
      existingContractTypeMaps: local!existingContractTypeMaps,
      contractTypeMaps: local!contractTypeMaps,
      originalClauseSetContractTypes: local!originalClauseSetContractTypes,
      clauseSetTemplate: local!clauseSetTemplate,
      originalClauseSetTemplate: local!originalClauseSetTemplate
    )
  )
)

