          newValues: if(
            fv!item.setId = ri!clauseSet.setId,
            fv!item.mappingId,
            null
          )
        )
      )
    ),
    a!save(
      ri!contractTypeMaps,
      ri!existingContractTypeMaps
    )
  }
)

