_a-0000ea79-8f1b-8000-9cb5-011c48011c48_.sail_code:
rule!AS_CO_UT_updateCdtFieldsByFieldValuePairs(
  cdt: ri!clauseSetContext,
  fieldValuePairs: {
    /*Resets the user field values if the usernames are invalid*/
    #"SYSTEM_SYSRULES_forEach"(
      /*Filtering out user fields to check for valid usernames*/
      items: intersection(
        a!keys(ri!clauseSetContext),
        cons!AS_GCM_TXT_CLAUSE_SET_USER_FIELDS
      ),
      expression: {
        field: fv!item,
        value: if(
          isusernametaken(property(ri!clauseSetContext, fv!item)),
          property(ri!clauseSetContext, fv!item),
          null()
        )
      }
    ),
    /*Defaulting Contract Format ID to UCF*/
    a!map(
      field: "refContractFormatId",
      value: cons!AS_GCM_REF_ID_CONTRACT_FORMAT_UCF
    )
  }
)
