      runtimetypeof(ri!records[ri!fieldValuePair.field]) = 'type!{http://www.appian.com/ae/types/2009}Document?list'
    ),
    then: wherecontains(
      cast(
        'type!{http://www.appian.com/ae/types/2009}Document',
        null
      ),
      ri!records[ri!fieldValuePair.field]
    ),
    whenTrue: ri!matchType = "CONTAINS",
    then: where(
      apply(
        rule!AS_CO_UT_ContainsValueByField(
          record: _,
          field: ri!fieldValuePair.field,
          value: touniformstring(ri!fieldValuePair.value)
        ),
        ri!records
      )
    ),
    whenTrue: ri!matchType = "EXACT",
    /*Standard filter logic*/
    then: wherecontains(
      touniformstring(ri!fieldValuePair.value),
      touniformstring(ri!records[ri!fieldValuePair.field])
    ),
    default: fn!error(
      "Invalid matchType, must be one of: EXACT, CONTAINS"
    )
  ),
  if(
    ri!matchingCriteria = "ANY",
    union(ri!previousIndices, local!newIndices),
    intersection(ri!previousIndices, local!newIndices)
  )
)

_a-0000eaf1-8805-8000-9cec-011c48011c48_.sail_code:
a!localVariables(
  local!updatedOrder: rule!AS_GCM_updateAddedClauseIdsMapBasedOnOrder(
    orderMap: ri!orderMap,
    ruleMap: a!map(
      includedClauseIds: ri!ruleIncludedClauseIds,
      excludedClauseIds: ri!ruleExcludedClauseIds
    ),
    templateMap: a!map(
      includedClauseIds: ri!templateIncludedClauseIds,
      excludedClauseIds: ri!templateExcludedClauseIds
    ),
    /* passing the null to excluded for both to maintain the map structure */
    farMatrixMap: a!map(
      includedClauseIds: ri!farMatrixClauseIds,
      excludedClauseIds: {}
    )
  ),
  if(
    a!isNullOrEmpty(local!updatedOrder),
    {},
    rule!AS_CO_UT_rejectNullValuesFromArray(
      array: rule!AS_CO_UT_distinct(
        {
          /* Combine all includedIds */
          rule!AS_CO_UT_indexPath(
            cdt: local!updatedOrder[1].values,
            field: "includedClauseIds"
          ),
          /* remove the first priority excludedIds from the combined ids */
          difference(
            tointeger(
              /* combine the updated ids with second priority includedIds*/
              rule!AS_CO_UT_distinct(
                /* remove the second priority excludedIds from the least priority includedIds*/
                {
                  difference(
                    tointeger(
                      rule!AS_CO_UT_indexPath(
                        cdt: local!updatedOrder[3].values,
                        field: "includedClauseIds"
                      )
                    ),
                    tointeger(
                      rule!AS_CO_UT_indexPath(
                        cdt: local!updatedOrder[2].values,
                        field: "excludedClauseIds"
                      )
                    )
                  ),
                  rule!AS_CO_UT_indexPath(
                    cdt: local!updatedOrder[2].values,
                    field: "includedClauseIds"
                  )
                }
              )
            ),
            tointeger(
              rule!AS_CO_UT_indexPath(
                cdt: local!updatedOrder[1].values,
                field: "excludedClauseIds"
              )
            )
          )
        }
      )
    )
  )
)

