_a-0000eb85-3dbf-8000-9d30-011c48011c48_.sail_code:
if(
  /* retain the clause set clause status if any below boolean is true() */
  or(
    ri!isCopyFillins,
    ri!isAmendmentModification,
    ri!isCreateAwardFromSolicitation
  ),
  ri!statusId,
  if(
    and(
      or(ri!isFillin, ri!isEditable),
      /*Validating IBR clauses based on "Ignore IBR Validation Errors" toggle*/
      if(
        rule!AS_GCM_IBRValidationDisabled_wrapper(),
        ri!formatId = cons!AS_GCM_REF_ID_FORMAT_FULL_TEXT,
        {}
      )
    ),
    if(
      a!isNullOrEmpty(ri!fillinStats),
      cons!AS_GCM_REF_ID_CLAUSE_SET_CLAUSE_STATUS_INCOMPLETE,
      /* check if all the fillins are completed */
      if(
        ri!fillinStats.totalFillinCount = ri!fillinStats.completedFillinCount,
        cons!AS_GCM_REF_ID_CLAUSE_SET_CLAUSE_STATUS_COMPLETE,
        cons!AS_GCM_REF_ID_CLAUSE_SET_CLAUSE_STATUS_INCOMPLETE
      )
    ),
    cons!AS_GCM_REF_ID_CLAUSE_SET_CLAUSE_STATUS_COMPLETE
  )
)

_a-0000e897-ddd7-8000-9c0c-011c48011c48_.sail_code:
a!localVariables(
  local!datasubset: todatasubset(
    /* Output Modifier   */
    rule!AS_GCM_updateDataByModifierMaps(
      data: ri!data,
      modifierMaps: ri!modifierMaps
    ),
    ri!pagingInfo
  ),
  a!match(
    value: ri!returnType,
    equals: cons!AS_CO_ENUM_QE_RETURN_TYPE_DATA_SUBSET,
    then: local!datasubset,
    /* When the data is null then just return the same    */
    whenTrue: rule!AS_CO_UT_isBlank(ri!data),
    then: ri!data,
    /* When the return type is SINGLE_OBJECT then type cast to its corresponding single object type   */
    equals: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT,
    then: rule!AS_CO_UT_cast(
      typeOf: a!flatten(ri!data)[1],
      value: index(local!datasubset.data, 1, null)
    ),
    default: rule!AS_CO_UT_cast(
      typeOf: ri!data,
      value: local!datasubset.data
    )
  )
)

