   'type!{http://www.appian.com/ae/types/2009}Text',
  if(
    isnull(ri!input),
    ri!input,
    substitute(
      ri!input,
      /*The standard argument format is `[%x]` where `x` is the argument position*/
      concat("[%", ri!index, "]"),
      fn!index(ri!arguments, ri!index, "")
    )
  )
)

_a-0000e828-e408-8000-9bf9-011c48011c48_.sail_code:
cast(
  'type!{http://www.appian.com/ae/types/2009}Integer?list',
  if(
    rule!AS_CO_UT_isBlank(ri!refClauseColumns),
    null,
    index(
      rule!AS_GCM_QR_getRefClause(
        fields: recordType!AS GCM R Clause.clauseId,
        logicalExpression: #"SYSTEM_SYSRULES_queryLogicalExpression"(
          operator: "AND",
          logicalexpressions: {
            /*Solutions Hub Toggle: 
                 True  - Include Null Usage
                 False - Exclude Null Usage*/
            if(
              rule!AS_GCM_DetermineFarMatrixEvaluationBehaviour_wrapper(),
              /*Applying this combination of logical expressions in order to avoid Clauses with null usage for all ref clause columns*/
              {
                /*Filtering Clauses that has optional in atleast one of the ref clause columns*/
                #"SYSTEM_SYSRULES_queryLogicalExpression"(
                  operator: "OR",
                  filters: {
                    #"SYSTEM_SYSRULES_forEach"(
                      items: ri!refClauseColumns,
                      expression: #"SYSTEM_SYSRULES_queryFilter"(
                        field: fv!item,
                        operator: "=",
                        value: cons!AS_GCM_REF_ID_USAGE_OPTIONAL
                      )
                    ),

                  },
                  ignorefilterswithemptyvalues: true
                ),
                #"SYSTEM_SYSRULES_forEach"(
                  items: ri!refClauseColumns,
                  /*Filtering Clauses that is not required or applicalbe or with null usage in the ref clause columns*/

                  expression: #"SYSTEM_SYSRULES_queryLogicalExpression"(
                    operator: "OR",
                    filters: {
                      #"SYSTEM_SYSRULES_queryFilter"(
                        field: fv!item,
                        operator: "not in",
                        value: {
                          cons!AS_GCM_REF_ID_USAGE_REQUIRED,
                          cons!AS_GCM_REF_ID_USAGE_APPLICABLE
                        }
                      ),
                      #"SYSTEM_SYSRULES_queryFilter"(field: fv!item, operator: "is null")
                    }
                  )
                )
              },
              /*Filtering Clauses that has optional usage in all of the ref clause columns*/
              #"SYSTEM_SYSRULES_queryLogicalExpression"(
                operator: "AND",
                filters: {
                  #"SYSTEM_SYSRULES_forEach"(
                    items: ri!refClauseColumns,
                    expression: #"SYSTEM_SYSRULES_queryFilter"(
                      field: fv!item,
                      operator: "=",
                      value: cons!AS_GCM_REF_ID_USAGE_OPTIONAL
                    )
                  )
                },
                ignorefilterswithemptyvalues: true
              )
            )
          },
          filters: {
            /*Filtering only clauses from FAR Matrix for awards*/
            if(
              ri!refDataId = cons!AS_GCM_REF_ID_CLAUSE_SET_TYPE_AWARD,
              #"SYSTEM_SYSRULES_queryFilter"(
                field: recordType!AS GCM R Clause.refTypeId,
                operator: "=",
                value: cons!AS_GCM_REF_ID_TYPE_CLAUSE
              ),
              {}
            ),
            #"SYSTEM_SYSRULES_queryFilter"(
              field: recordType!AS GCM R Clause.isActive,
              operator: "=",
              value: true
            )
          },
          ignorefilterswithemptyvalues: true
        )
      ),
      recordType!AS GCM R Clause.clauseId,
      null
    )
  )
)

_a-0000e63a-a5cb-8000-9bc6-011c48011c48_.sail_code:
a!localVariables(
  local!bundle: rule!AS_GCM_CO_UT_loadBundleFromFolder(langISOCode: null()),
  local!orderMap: rule!AS_GCM_clauseInclusionAndExclusionLogicForClauseSet_wrapper(),
  local!allClauseSetClauses: index(
