  /*Clauses based on provide details fields*/
  local!refClauseColumns: rule!AS_GCM_BL_returnSmartMatrixColumnForRefClauses(
    clauseSet: ri!clauseSet,
    contractTypeMaps: local!contractType
  ),
  local!provideDetailsReqClauses: rule!AS_GCM_BL_provideDetailsReqClausesLogic(
    refClauseColumns: local!refClauseColumns,
    clauseSet: ri!clauseSet
  ),
  local!questionnaireClauses: rule!AS_GCM_BL_returnClausesBasedOnQuestionnaire(
    clauseSet: ri!clauseSet,
