                )
              }
            )
          }
        )
      },
      buttons: rule!AS_CO_CPS_cancelSaveButtons(
        confirmLabel: "",
        cancelLabel: "Cancel",
        cancelAccessibilityText: "",
        confirmAccessibilityText: "",
        cancelSaveInto: {
          a!save(
            ri!userAction,
            cons!AS_CO_ENUM_USER_ACTION_CANCEL
          )
        },
        confirmSaveInto: {
          a!save(
            ri!userAction,
            cons!AS_CO_ENUM_USER_ACTION_SUBMIT
          ),
          a!save(
            local!clauseSetClauses,
            rule!AS_CO_UT_captureMetadata(
              cdt: rule!AS_CO_UT_rejectNullValuesFromArray(
                array: {
                  /*Constructing new clauses*/
                  #"SYSTEM_SYSRULES_forEach"(
                    items: rule!AS_GCM_QR_getRefClause(
                      clauseIds: difference(
                        tointeger(local!selectedClauses),
                        tointeger(local!oldSelectedClauseIds)
                      ),
                      fields: {
                        recordType!AS GCM R Clause.refFormat,
                        recordType!AS GCM R Clause.ucfSection,
                        if(
                          rule!AS_CO_UT_booleanDefaultFalse(local!isNotUCF),
                          {
                            recordType!AS GCM R Clause.additionalSections,
                            recordType!AS GCM R Clause.additionalSections
                          },
                          {}
                        )
                      },
                      returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
                      relatedRecordData: if(
                        rule!AS_CO_UT_booleanDefaultFalse(local!isNotUCF),
                        #"SYSTEM_SYSRULES_relatedRecordData"(
                          relationship: recordType!AS GCM R Clause.additionalSections,
                          filters: {
                            #"SYSTEM_SYSRULES_queryFilter"(
                              field: recordType!AS GCM R Clause Section Mapping.refSection.contractFormatId,
                              operator: "=",
                              value: ri!clauseSet.refContractFormat.refDataId
                            )
                          }
                        ),
                        {}
                      )
                    ),
                    expression: rule!AS_CO_UT_cast(
                      type: 'type!{urn:com:appian:types:AS:GCM}AS_GCM_ClauseSet_Clause',
                      value: {
                        refClause: fv!item,
                        setId: ri!clauseSet.setId,
                        /* Updating the refAddedVia as "Manually" irrespective of the clauses */
                        refAddedVia: {
                          refDataId: cons!AS_GCM_REF_ID_CLAUSE_ADDED_VIA_MANUALLY
                        },
                        refFormat: fv!item[recordType!AS GCM R Clause.refFormat],
                        format: fv!item[recordType!AS GCM R Clause.refFormat],
                        section: if(
                          and(
                            rule!AS_CO_UT_booleanDefaultFalse(local!isNotUCF),
                            rule!AS_CO_UT_isBlank(
                              fv!item[recordType!AS GCM R Clause.additionalSections.refSection.sectionLabel]
                            )
                          ),
                          null,
                          rule!AS_CO_UT_cast(
                            type: 'type!{urn:com:appian:types:AS:GCM}AS_GCM_ClauseSet_ClauseSection',
                            value: {
                              sectionLabel: if(
                                rule!AS_CO_UT_booleanDefaultFalse(local!isNotUCF),
                                fv!item[recordType!AS GCM R Clause.additionalSections.refSection.sectionLabel],
                                fv!item[recordType!AS GCM R Clause.ucfSection.sectionLabel]
                              ),
                              refSection: if(
                                rule!AS_CO_UT_booleanDefaultFalse(local!isNotUCF),
                                fv!item[recordType!AS GCM R Clause.additionalSections],
                                fv!item[recordType!AS GCM R Clause.ucfSection]
                              ),
                              isActive: true
                            }
                          )
                        ),
                        isActive: true(),
                        refUsage: a!match(
                          value: fv!item[recordType!AS GCM R Clause.clauseId],
                          whenTrue: contains(
                            tointeger(
                              local!provideDetailsApplicableClauseIds
                            ),
                            fv!value
                          ),
                          then: rule!AS_GAM_filterCdtOrRecordDataByField(
                            cdtOrRecord: local!refUsageData,
                            field: recordType!AS GCM R Data.refDataId,
                            value: cons!AS_GCM_REF_ID_USAGE_APPLICABLE
                          ),
                          whenTrue: contains(
                            tointeger(local!provideDetailsOptionalClauseIds),
                            fv!value
                          ),
                          then: rule!AS_GAM_filterCdtOrRecordDataByField(
                            cdtOrRecord: local!refUsageData,
                            field: recordType!AS GCM R Data.refDataId,
                            value: cons!AS_GCM_REF_ID_USAGE_OPTIONAL
                          ),
                          whenTrue: contains(
                            tointeger(
                              rule!AS_CO_UT_indexPath(
                                cdt: local!excludedClauses,
                                field: "refClause.clauseId",
                                indexDefault: ""
                              )
                            ),
                            fv!value
                          ),
                          then: rule!AS_GAM_filterCdtOrRecordDataByField(
                            cdtOrRecord: local!refUsageData,
                            field: recordType!AS GCM R Data.refDataId,
                            value: cons!AS_GCM_REF_ID_USAGE_REQUIRED
                          ),
                          default: null
                        ),
                        isEditedViaEditClause: false(),
                        clauseTitle: fv!item[recordType!AS GCM R Clause.clauseTitle],
                        alternateValue: fv!item[recordType!AS GCM R Clause.alternateValue],
                        effectiveDate: fv!item[recordType!AS GCM R Clause.effectiveDate],
                        isClauseUpdated: false()
                      }
                    )
                  ),
                  /*Updating the refUsage for readded excluded clauses*/
                  rule!AS_CO_UT_updateCdtFields(
                    cdt: rule!AS_GCM_QE_getClauseSetClauseDetailsWithBatching(
                      returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
                      relationIds: rule!AS_CO_UT_indexWhere(
                        value: intersection(
                          local!excludedClauseIds,
                          local!selectedClauses
                        ),
                        initialArray: local!excludedClauseIds,
                        finalArray: index(local!excludedClauses, "relationId", {})
                      ),
                      onlyGetFieldsForWrite: true(),
                      showInactive: true(),
                      batchingSize: cons!AS_GCM_INT_QUERY_BATCH_SIZE_LARGE
                    ),
                    fields: { "refUsage" },
                    newValues: { null() }
                  )
                }
              ),
              modifiedBy: loggedInUser()
            )
          ),
          #"SYSTEM_SYSRULES_forEach"(
            items: local!selectedClauses,
            expression: rule!AS_GCM_MTR_SAVE_addManualClausesFromAddClausesRelatedAction()
          ),
          a!save(
            ri!clauseSetClauses,
            local!clauseSetClauses
          )
        },
        confirmSubmit: true(),
        cancelSubmit: true(),
        confirmDisabled: rule!AS_CO_UT_isBlank(local!selectedClauses)
      )
    )
  )
)

