/*Note: Assertions cannot be added to this rule as it requires data from DSE*/
rule!AS_GAM_UT_queryEntityWithSelection(
  entity: cons!AS_GCM_ENT_CLAUSE_SET_CLAUSE_TEXT,
  entityType: 'type!{urn:com:appian:types:AS:GCM}AS_GCM_ClauseSet_Clause_Text',
  returnType: ri!returnType,
  executeWhen: and(
    ri!executeWhen,
    or(
      any(
        rule!AS_CO_UT_isNotBlank(_),
        {
          ri!additionalFilters,
          ri!logicalExpression,
          ri!selectionColumns
        }
      ),
      rule!AS_CO_UT_isNotBlank(ri!relationId),
      rule!AS_CO_UT_isNotBlank(ri!setId)
    )
  ),
  /* To ensure proper null checks, we'll force all queryFilter values to not be null */
  logicalExpression: ri!logicalExpression,
  pagingInfo: ri!pagingInfo,
  filters: {
    #"SYSTEM_SYSRULES_queryFilter"(
      field: "relationId",
      operator: "in",
      value: tointeger(ri!relationId)
    ),
    #"SYSTEM_SYSRULES_queryFilter"(
      field: "setId",
      operator: "=",
      value: ri!setId
    ),
    ri!additionalFilters
  },
  selectionColumns: ri!selectionColumns
)

