        rule!AS_GCM_UT_getClauseSetClauseTextsWithQuerySelection(
          relationId: fv!item.relationId,
          returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT,
          executeWhen: rule!AS_CO_UT_isNotBlank(fv!item.relationId),
          selectionColumns: "clauseText",
