            startIndex: 1,
            batchSize: 1,
            sort: #"SYSTEM_SYSRULES_sortInfo"(field: "relationId", ascending: false)
          )
        ).data,
        "clauseText",
        null
      ),
      {
        rule!AS_GCM_UT_createBulletedListForUCFClauses(
          bulletedText: rule!AS_GCM_UT_removeEmptyaragraphs(
            text: rule!AS_GCM_substituteListFromTextUsingReduce(
              text: rule!AS_GCM_UT_convertHtmlToOoxml(
                text: rule!AS_GCM_formatClauseTitleForDocument(clause: fv!item) & local!clauseText
              ),
              find: {
                "<w:p></w:p>",
                "<w:p><w:tbl>",
                "</w:tbl></w:p>",
                "</w:r><w:tbl>",
                "</w:tbl><w:r",
                "&nbsp",
                cons!AS_GCM_TEXT_NULL_TABLE_TEXT,

              },
              replaceWith: {
                "",
                "<w:tbl>",
                "</w:tbl>",
                "</w:r></w:p><w:tbl>",
                "</w:tbl><w:p><w:r",
                "",
                ""
              },
              replaceAllInstances: true()
            )
          )
        )
      }
    )
  )
)

_a-0000e67d-efac-8000-9bcd-011c48011c48_.sail_code:
a!localVariables(
  local!index: /*tointeger(
    wherecontains(ri!text, touniformstring(ri!list.text))
  )*/tointeger(ri!parallelTextIndex),
  local!enumerate: enumerate(local!index) + 1,
  local!prevTextIndex: /*index(
    if(
      ri!itemCount <= 1,
      0,
      index(
        ri!allTextIndices,
        wherecontains(local!index, ri!allTextIndices) - 1,
        {}
      )
    ),
    1,
    {}
  )*/ri!prevTextIndex,
  local!seq: #"SYSTEM_SYSRULES_forEach"(
    items: local!enumerate,
    expression: if(
      fv!item > tointeger(local!prevTextIndex),
      fv!item,
      null
    )
  ),
  local!finalEnum: reject(
    rule!AS_CO_UT_isBlank(value: _),
    { local!seq }
  ),
  a!map(
    tag: joinarray(
      index(ri!list.tag, local!finalEnum, {}),
      ", "
    ),
    text: ri!text,
    font: substitute(
      substitute(
        joinarray(
          index(ri!list.font, local!finalEnum, {}),
          ", "
        ),
        "0, ",
        ""
      ),
      ", 0",
      ""
    ),
    endTag: if(
      rule!AS_CO_UT_isBlank(index(ri!list.endTag, local!index + 1, {})),
      "",
      joinarray(
        index(ri!list.endTag, local!index + 1, {}),
        ""
      )
    ),
    color: joinarray(
      union(
        index(ri!list.color, local!finalEnum, {}),
        index(ri!list.color, local!finalEnum, {})
      ),
      ""
    ),
    backgroundColor: joinarray(
      union(
        index(
          ri!list.backgroundColor,
          local!finalEnum,
          {}
        ),
        index(
          ri!list.backgroundColor,
          local!finalEnum,
          {}
        )
      ),
      ""
    ),
    bulletedListNum: rule!AS_GCM_UT_findBulletedLevel(
      tag: joinarray(
        index(ri!list.tag, local!finalEnum, {}),
        ", "
      )
    ),
    alignment: joinarray(
      index(ri!list.alignment, local!finalEnum, {}),
      ""
    ),
    indent: joinarray(
      index(ri!list.indent, local!finalEnum, {}),
      ""
    ),
    table: {
      tag: joinarray(
        reject(
          rule!AS_GCM_UT_doesNotContain(array: { "tr", "td", "table", "th" }, value: _),
          index(ri!list.tag, local!finalEnum, {})
        ),
        ", "
      ),
      isNewTable: contains(
        touniformstring(index(ri!list.tag, local!finalEnum, {})),
        "table"
      ),
      isNewRow: and(or(
        contains(
          touniformstring(index(ri!list.tag, local!finalEnum, {})),
          "tr"
        ),
        contains(
          touniformstring(index(ri!list.tag, local!finalEnum, {})),
          "table"
        )
      ), not(contains(
        touniformstring(index(ri!list.tag, local!finalEnum, {})),
        "caption"
      ))),
      isNewColumn: or(
        contains(
          touniformstring(index(ri!list.tag, local!finalEnum, {})),
          "td"
        ),
        contains(
          touniformstring(index(ri!list.tag, local!finalEnum, {})),
          "th"
        ),
        contains(
          touniformstring(index(ri!list.tag, local!finalEnum, {})),
          "table"
        )
      ),
      text: if(contains(
        touniformstring(index(ri!list.tag, local!finalEnum, {})),
        "caption"
      ), "", ri!text),
      caption: if(contains(
        touniformstring(index(ri!list.tag, local!finalEnum, {})),
        "caption"
      ), ri!text, ""),
      endTag: joinarray(
        reject(
          rule!AS_GCM_UT_doesNotContain(array: {"/th", "/tr", "/td", "/table" }, value: _),
          index(
            ri!list.endTag,
            {
              local!finalEnum,
              /*tointeger(local!finalEnum) + 1,
              tointeger(local!finalEnum) + 2,
              tointeger(local!finalEnum) + 3,
              tointeger(local!finalEnum) + 4,
              tointeger(local!finalEnum) + 5,*/
              /*tointeger(local!finalEnum) + 6,
              tointeger(local!finalEnum) + 7,
              tointeger(local!finalEnum) + 8,
              tointeger(local!finalEnum) + 9,
              tointeger(local!finalEnum) + 10,
              tointeger(local!finalEnum) + 11,
              tointeger(local!finalEnum) + 12*/
            },
            {}
          )
        ),
        ", "
      )
    }
  )
)

