/*Take the input of a cdt, field, and value and returns the matching cdt data

ex. cdt of users, filter by field isActive and value of true to find your active users
*/
cast(
  typeof(ri!cdt),
  index(
    a!flatten(ri!cdt),
    rule!AS_CO_UT_returnCdtIndicesByField(
      cdt: ri!cdt,
      field: ri!field,
      value: ri!value
    ),
    {}
  )
)

f28850a6-d0cc-4841-a587-a36a568d70a3.sail_code:
a!localVariables(
  /*Returning only nested fields to null nested empty cdts*/
  local!nestedFieldNames: difference(
    touniformstring(
      rule!AS_CO_UT_returnCdtFields(
        typeOf: ri!cdt,
        onlyPrimitiveFields: false()
      )
    ),
    touniformstring(
      rule!AS_CO_UT_returnCdtFields(
        typeOf: ri!cdt,
        onlyPrimitiveFields: true()
      )
    )
  ),
  #"SYSTEM_SYSRULES_forEach"(
    items: ri!cdt,
    expression: rule!AS_CO_UT_updateCdtFieldsByFieldValuePairs(
      cdt: fv!item,
      fieldValuePairs: a!localVariables(
        local!cdt: fv!item,
        #"SYSTEM_SYSRULES_forEach"(
          items: local!nestedFieldNames,
          expression: {
            field: fv!item,
            value: if(
              rule!AS_CO_UT_isBlank(
                rule!AS_CO_UT_indexPath(
                  cdt: local!cdt,
                  field: fv!item & "." & rule!AS_GCM_UT_getPrimaryKeyFieldByType(cdt: index(local!cdt, fv!item)),
                  indexDefault: null()
                )
              ),
              null,
              index(local!cdt, fv!item, null())
            )
          }
        )
      )
    )
  )
)

_a-0000e2ad-62e7-8000-9ba2-011c48011c48_.sail_code:
cast(
  typeof(ri!cdt),
  if(
    rule!AS_CO_UT_isBlank(
      ri!field
    ),
    ri!cdt,
    with(
      local!dataSortedByField: todatasubset(
        ri!cdt,
        #"SYSTEM_SYSRULES_pagingInfo"(
          startIndex: 1,
          batchSize: - 1,
          sort: #"SYSTEM_SYSRULES_sortInfo"(
            field: ri!field,
            ascending: rule!AS_CO_UT_replaceNull(
              nullableValue: ri!ascending,
              replacementValue: true
            )
          )
        )
      ).data,
      if(
        rule!AS_CO_UT_isBlank(
          ri!nullsFirst
        ),
        local!dataSortedByField,
        with(
          local!cdtsWithNullFieldValue: rule!AS_CO_UT_filterCdtByField(
            cdt: local!dataSortedByField,
            field: ri!field,
            value: null
          ),
          local!cdtsWithNonNullFieldValue: rule!AS_CO_UT_rejectCdtByField(
            cdt: local!dataSortedByField,
            field: ri!field,
            value: null
          ),
          if(
            ri!nullsFirst,
            append(
              local!cdtsWithNullFieldValue,
              local!cdtsWithNonNullFieldValue
            ),
            append(
              local!cdtsWithNonNullFieldValue,
              local!cdtsWithNullFieldValue
            )
          )
        )
      )
    )
  )
)

