a!localVariables(
  local!htmlTag: touniformstring(
    split(
      index(
        index(ri!list, "tag", ""),
        /*wherecontains(
          ri!text,
          touniformstring(index(ri!list, "text", {}))
        )*/ri!index,
        {}
      ),
      ", "
    )
  ),
  local!endTag: index(
    index(ri!list, "endTag", ""),
    /*wherecontains(
      ri!text,
      touniformstring(index(ri!list, "text", {}))
    )*/ri!index,
    {}
  ),
  local!tableTag: index(
    index(
      ri!list,
      /*wherecontains(
        ri!text,
        touniformstring(index(ri!list, "text", {}))
      )*/ri!index,
      {}
    ),
    "table",
    {}
  ),
  local!isNewTable: if(
    toboolean(local!tableTag.isNewTable) = true,
    true,
    false
  ),
  if(
    local!isNewTable,
    rule!AS_GCM_UT_dynamicTableInClauseText(formattedList: ri!list, index: ri!index),
    rule!AS_GCM_UT_clauseTextOoxmlElements(
      text: concat(
        if(
          contains(local!htmlTag, "div"),
          if(
            or(
              contains(local!htmlTag, "p"),
              contains(local!htmlTag, "br"),
              rule!AS_CO_UT_isNotBlank(
                rule!AS_GCM_UT_determineHeadingType(headingTag: local!htmlTag)
              ),
              if(
                or(
                  #"SYSTEM_SYSRULES_forEach"(
                    items: { "ol", "ul", "li" },
                    expression: contains(local!htmlTag, fv!item)
                  )
                ),
                rule!AS_CO_UT_isNotBlank(
                  index(
                    index(ri!list, "bulletedListNum", {}),
                    /* wherecontains(
                      ri!text,
                      touniformstring(index(ri!list, "text", {}))
                    )*/ri!index,
                    {}
                  )
                ),
                null
              ),
              rule!AS_CO_UT_isNotBlank(
                index(
                  index(ri!list, "alignment", {}),
                  /*wherecontains(
                    ri!text,
                    touniformstring(index(ri!list, "text", {}))
                  )*/ri!index,
                  {}
                )
              ),
              rule!AS_CO_UT_isNotBlank(
                index(
                  index(ri!list, "indent", {}),
                  /*wherecontains(
                    ri!text,
                    touniformstring(index(ri!list, "text", {}))
                  )*/ri!index,
                  {}
                )
              )
            ),
            "",
            rule!AS_GCM_UT_docxLineBreak(num: 1)
          ),
          ""
        ),
        regexreplaceall(
          "&linebrk",
          regexreplaceall(
            "&nbsp;",
            regexreplaceall(
              "&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;",
              rule!AS_GCM_UT_encodeSpecialCharactersToXML(
                text: ri!text,
                preserveWords: cons!AS_GCM_TXT_PRESERVE_WORDS_FOR_SPL_CHAR_ENCODING
              ),
              /*rule!AS_GCM_UT_docxLineBreak(num: 1) & "</w:t><w:t>"*/" "
            ),
            " "
          ),
          rule!AS_GCM_UT_docxLineBreak(num: 1) & "</w:t><w:t>"
        )
      ),
      fontSize: if(
        contains(local!htmlTag, "h1"),
        46,
        index(
          index(ri!list, "font", {}),
          /*wherecontains(
            ri!text,
            touniformstring(index(ri!list, "text", {}))
          )*/ri!index,
          {}
        )
      ),
      textColor: index(
        index(
          index(ri!list, "color", {}),
          /*wherecontains(
            ri!text,
            touniformstring(index(ri!list, "text", {}))
          )*/ri!index,
          {}
        ),
        1,
        {}
      ),
      backgroundColor: index(
        index(ri!list, "backgroundColor", {}),
        /*wherecontains(
            ri!text,
            touniformstring(index(ri!list, "text", {}))
          )*/ri!index,
        {}
      ),
      isBold: if(
        or(
          contains(local!htmlTag, "b"),
          contains(local!htmlTag, "strong")
        ),
        true,
        false
      ),
      isItalic: if(
        or(
          contains(local!htmlTag, "em"),
          contains(local!htmlTag, "i")
        ),
        true,
        false
      ),
      isStrike: if(contains(local!htmlTag, "s"), true, false),
      isUnderline: if(contains(local!htmlTag, "u"), true, false),
      isNewParagraph: if(
        or(
          and(
            contains(local!htmlTag, "p"),
            ri!text <> " "
          ),
          rule!AS_CO_UT_isNotBlank(
            rule!AS_GCM_UT_determineHeadingType(headingTag: local!htmlTag)
          ),
          if(
            or(
              #"SYSTEM_SYSRULES_forEach"(
                items: { "ol", "ul", "li" },
                expression: contains(local!htmlTag, fv!item)
              )
            ),
            rule!AS_CO_UT_isNotBlank(
              index(
                index(ri!list, "bulletedListNum", {}),
                wherecontains(
                  ri!text,
                  touniformstring(index(ri!list, "text", {}))
                ),
                {}
              )
            ),
            null
          ),
          rule!AS_CO_UT_isNotBlank(
            index(
              index(ri!list, "alignment", {}),
              wherecontains(
                ri!text,
                touniformstring(index(ri!list, "text", {}))
              ),
              {}
            )
          ),
          rule!AS_CO_UT_isNotBlank(
            index(
              index(ri!list, "indent", {}),
              wherecontains(
                ri!text,
                touniformstring(index(ri!list, "text", {}))
              ),
              {}
            )
          )
        ),
        true(),
        false()
      ),
      headingType: rule!AS_GCM_UT_determineHeadingType(headingTag: local!htmlTag),
      bulletedList: if(
        or(
          #"SYSTEM_SYSRULES_forEach"(
            items: { "ol", "ul", "li" },
            expression: contains(local!htmlTag, fv!item)
          )
        ),
        index(
          index(ri!list, "bulletedListNum", {}),
          wherecontains(
            ri!text,
            touniformstring(index(ri!list, "text", {}))
          ),
          {}
        ),
        null
      ),
      alignment: index(
        index(ri!list, "alignment", {}),
        wherecontains(
          ri!text,
