a!localVariables(
  local!table: #"SYSTEM_SYSRULES_forEach"(
    items: ri!list.table,
    expression: {
      if(
        rule!AS_CO_UT_isBlank(fv!item.tag),
        {},
        fv!item
      )
    }
  ),
  /*local!table: reject(
    rule!AS_CO_UT_isBlank(value: _),
    {local!tableTags}
  ),*/
  local!newRowIndex: wherecontains(true, toboolean(local!table.isNewRow)),
  local!numberOfColumns: if(
    count(local!newRowIndex) = 1,
    count(where(ri!list.table.isNewColumn)),
    #"SYSTEM_SYSRULES_forEach"(
      items: local!newRowIndex,
      expression: {
        tointeger(index(local!newRowIndex, fv!index + 1, {})) - fv!item
      }
    )
  ),
  local!highestColumnCount: index(
    {
      rule!AS_CO_UT_sortArray(
        array: tointeger(local!numberOfColumns),
        ascending: false
      )
    },
    1,
    null
  ),
  #"SYSTEM_SYSRULES_forEach"(
    items: local!newRowIndex,
    expression: {
      index(
        local!table.text,
        index(
          enumerate(fv!item + local!highestColumnCount) + 1,
          enumerate(local!highestColumnCount) + fv!item,
          {}
        ),
        ""
      )
    }
  )
)

