_a-0000eb72-c933-8000-9d28-011c48011c48_.sail_code:
rule!AS_GCM_CRD_collapsibleSection(
  dynamicFieldProperties: ri!dynamicFieldProperties,
  sectionLabel: "",
  contents: #"SYSTEM_SYSRULES_cardLayout"(
    contents: {
      #"SYSTEM_SYSRULES_columnsLayout"(
        columns: {
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: rule!AS_GCM_DPL_CO_dropdownField(
              label: ri!dynamicFieldProperties.refContractFormat.label,
              required: ri!dynamicFieldProperties.refContractFormat.required,
              readOnly: ri!dynamicFieldProperties.refContractFormat.readOnly,
              placeholder: ri!dynamicFieldProperties.refContractFormat.placeholder,
              choiceLabels: rule!AS_GAM_translateMultipleBundleKeys(
                bundle: ri!bundle,
                bundleKeys: ri!contractFormatData[recordType!AS GCM R Data.refKey]
              ),
              choiceValues: rule!AS_CO_UT_cast(
                type: 'type!{urn:com:appian:types:AS:GCM}AS_GCM_R_Data?list',
                value: ri!contractFormatData
              ),
              value: ri!clauseSet.refContractFormat,
              validations: ri!dynamicFieldProperties.refContractFormat.validations,
              saveInto: { ri!clauseSet.refContractFormat },
              labelPosition: "ABOVE"
            )
          ),
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {
              #"SYSTEM_SYSRULES_columnsLayout"(
                columns: {
                  #"SYSTEM_SYSRULES_columnLayout"(
                    contents: rule!AS_CO_CP_radioButtonField(
                      label: ri!dynamicFieldProperties.refType.label,
                      required: ri!dynamicFieldProperties.refType.required,
                      readOnly: ri!dynamicFieldProperties.refType.readOnly,
                      choiceLabels: rule!AS_GAM_translateMultipleBundleKeys(
                        bundle: ri!bundle,
                        bundleKeys: index(ri!typeData, "refKey", null)
                      ),
                      choiceValues: ri!typeData,
                      value: ri!clauseSet.refType,
                      validations: ri!dynamicFieldProperties.refType.validations,
                      saveInto: {
                        if(
                          and(
                            rule!AS_CO_UT_isBlank(value: ri!clauseSetContext),
                            rule!AS_CO_UT_isNotBlank(input: ri!clauseSet.refType)
                          ),
                          {
                            a!save(ri!clauseSet.isExistingVehicle, null),
                            a!save(ri!clauseSet.referencedIdv, null)
                          },
                          {}
                        ),
                        ri!clauseSet.refType,
                        a!save(ri!clauseSet.refCategory, null),

                      },
                      labelPosition: "ABOVE",
                      choiceLayout: "COMPACT"
                    )
                  ),
                  #"SYSTEM_SYSRULES_columnLayout"(
                    contents: rule!AS_CO_CP_radioButtonField(
                      label: ri!dynamicFieldProperties.refCategory.label,
                      required: ri!dynamicFieldProperties.refCategory.required,
                      readOnly: ri!dynamicFieldProperties.refCategory.readOnly,
                      choiceLabels: if(
                        ri!clauseSet.refType.refDataId = cons!AS_GCM_REF_ID_CLAUSE_SET_TYPE_AWARD,
                        rule!AS_GAM_translateMultipleBundleKeys(
                          bundle: ri!bundle,
                          bundleKeys: index(ri!awardCategoryData, "refKey", null)
                        ),
                        rule!AS_GAM_translateMultipleBundleKeys(
                          bundle: ri!bundle,
                          bundleKeys: index(
                            ri!solicitationCategoryData,
                            "refKey",
                            null
                          )
                        )
                      ),
                      choiceValues: if(
                        ri!clauseSet.refType.refDataId = cons!AS_GCM_REF_ID_CLAUSE_SET_TYPE_AWARD,
                        ri!awardCategoryData,
                        ri!solicitationCategoryData
                      ),
                      value: if(
                        /* defaulting the value to New on readonly */
                        ri!dynamicFieldProperties.refCategory.readOnly,
                        if(
                          ri!clauseSet.refType.refDataId = cons!AS_GCM_REF_ID_CLAUSE_SET_TYPE_AWARD,
                          ri!awardCategoryData[1],
                          ri!solicitationCategoryData[1]
                        ),
                        ri!clauseSet.refCategory
                      ),
                      validations: ri!dynamicFieldProperties.refCategory.validations,
                      saveInto: {
                        ri!clauseSet.refCategory,
                        rule!AS_GCM_UT_clausesetSaveForAmendmentOrModification(
                          clauseSet: ri!clauseSet,
                          questionnaire: ri!questionnaire,
                          existingClauseSetDetails: ri!existingClauseSetDetails,
                          existingContractTypeMaps: ri!existingContractTypeMaps,
                          isExistingId: ri!isExistingId,
                          contractTypeMaps: ri!contractTypeMaps,
                          isCategory: true(),
                          clauseSetContext: ri!clauseSetContext
                        ),
                        if(
                          ri!dynamicFieldProperties.categoryNumber.readOnly,
                          {},
                          a!save(ri!clauseSet.categoryNumber, null)
                        )
                      },
                      labelPosition: "ABOVE",
                      choiceLayout: "COMPACT",
                      showWhen: or(
                        rule!AS_CO_UT_isNotBlank(ri!clauseSet.refType),
                        ri!dynamicFieldProperties.refCategory.readOnly
                      )
                    )
                  )
                }
              )
            }
          )
        }
      ),
      #"SYSTEM_SYSRULES_columnsLayout"(
        columns: {
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {
              /* Solicitation / Award ID */
              rule!AS_GCM_DPL_CO_INP_textField(
                label: ri!dynamicFieldProperties.setNumber.label,
                required: ri!dynamicFieldProperties.setNumber.required,
                readOnly: ri!dynamicFieldProperties.setNumber.readOnly,
                value: ri!clauseSet.setNumber,
                validations: ri!dynamicFieldProperties.setNumber.validations,
                saveInto: {
                  ri!clauseSet.setNumber,
                  rule!AS_GCM_UT_clausesetSaveForAmendmentOrModification(
                    clauseSet: ri!clauseSet,
                    questionnaire: ri!questionnaire,
                    existingClauseSetDetails: ri!existingClauseSetDetails,
                    existingContractTypeMaps: ri!existingContractTypeMaps,
                    isExistingId: ri!isExistingId,
                    contractTypeMaps: ri!contractTypeMaps,
                    isCategory: false(),
                    clauseSetContext: ri!clauseSetContext
                  )
                },
                labelPosition: "ABOVE",
                placeholder: ri!dynamicFieldProperties.setNumber.placeholder,
                characterLimit: ri!dynamicFieldProperties.setNumber.characterLimit
              )
            }
          ),
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {
              /* Amendment / Modification Number*/
              rule!AS_GCM_DPL_CO_INP_textField(
                label: ri!dynamicFieldProperties.categoryNumber.label,
                required: ri!dynamicFieldProperties.categoryNumber.required,
                readOnly: ri!dynamicFieldProperties.categoryNumber.readOnly,
                value: ri!clauseSet.categoryNumber,
                validations: ri!dynamicFieldProperties.categoryNumber.validations,
                saveInto: { ri!clauseSet.categoryNumber },
                labelPosition: "ABOVE",
                placeholder: ri!dynamicFieldProperties.categoryNumber.placeholder,
                showWhen: and(
                  not(
                    ri!dynamicFieldProperties.refCategory.readOnly
                  ),
                  or(
                    ri!clauseSet.refCategory.refDataId = cons!AS_GCM_REF_ID_CATEGORY_AMENDMENT,
                    ri!clauseSet.refCategory.refDataId = cons!AS_GCM_REF_ID_CATEGORY_MODIFICATION
                  )
                ),
                characterLimit: ri!dynamicFieldProperties.categoryNumber.characterLimit
              )
            }
          )
        }
      ),
      #"SYSTEM_SYSRULES_columnsLayout"(
        columns: {
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {
              /* Contracting Officer */
              rule!AS_CO_CP_pickerFieldUsers(
                label: ri!dynamicFieldProperties.contractingOfficer.label,
                required: ri!dynamicFieldProperties.contractingOfficer.required,
                requiredMessage: ri!dynamicFieldProperties.contractingOfficer.requiredMessage,
                readOnly: ri!dynamicFieldProperties.contractingOfficer.readOnly,
                maxSelections: ri!dynamicFieldProperties.contractingOfficer.maxSelections,
                groupFilter: cons!AS_GAM_GRP_CONTRACTING_OFFICER,
                value: ri!clauseSet.contractingOfficer,
                saveInto: ri!clauseSet.contractingOfficer,
                labelPosition: "ABOVE",
                placeholder: ri!dynamicFieldProperties.contractingOfficer.placeholder
              )
            }
          ),
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {
              /* Contract Specialist */
              rule!AS_CO_CP_pickerFieldUsers(
                label: ri!dynamicFieldProperties.contractSpecialist.label,
                required: ri!dynamicFieldProperties.contractSpecialist.required,
                requiredMessage: ri!dynamicFieldProperties.contractSpecialist.requiredMessage,
                readOnly: ri!dynamicFieldProperties.contractSpecialist.readOnly,
                maxSelections: ri!dynamicFieldProperties.contractSpecialist.maxSelections,
                groupFilter: cons!AS_GAM_GRP_CONTRACT_SPECIALIST,
