a!localVariables(
  local!clauseSetContext: rule!AS_GCM_DefaultClauseSetContextFields(
    clauseSetContext: ri!clauseSetContext
  ),
  /*The exact below keys should be passed from GCW*/
  local!refDataFieldsMap: rule!AS_GAM_createMap(
    /*Represents the key in the context map that is passed from GCW*/
    keys: {
      "refTypeId",
      "refCategoryId",
      "refContractMethodId",
      "refContractFormatId"
    },
    /*Represents the fields in GCA*/
    values: {
      "refType",
      "refCategory",
      "refContractMethod",
      "refContractFormat"
    }
  ),
  local!refData: rule!AS_GCM_QR_getRefData(
    refDataId: property(
      local!clauseSetContext,
      a!keys(local!refDataFieldsMap)
    )
  ),
  rule!AS_CO_UT_updateCdtFieldsByFieldValuePairs(
    cdt: ri!clauseSet,
    fieldValuePairs: {
      /*Updating only simple fields*/
      #"SYSTEM_SYSRULES_forEach"(
        items: intersection(
          a!keys(local!clauseSetContext),
          a!keys(
            'type!{urn:com:appian:types:AS:GCM}AS_GCM_ClauseSet'()
          )
        ),
        expression: {
          field: fv!item,
          value: rule!AS_CO_UT_cast(
            typeOf: index(ri!clauseSet, fv!item, null()),
            value: a!defaultValue(
              value: property(local!clauseSetContext, fv!item, null()),
              default: index(ri!clauseSet, fv!item, null())
            )
          )
        }
      ),
      /*Updating the refData fields*/
      #"SYSTEM_SYSRULES_forEach"(
        items: intersection(
          a!keys(local!clauseSetContext),
          a!keys(local!refDataFieldsMap)
        ),
        expression: {
          field: local!refDataFieldsMap[fv!item],
          value: if(
            a!isNullOrEmpty(property(local!clauseSetContext, fv!item)),
            index(
              ri!clauseSet,
              local!refDataFieldsMap[fv!item],
              null()
            ),
            rule!AS_CO_UT_filterCdtByField(
              cdt: local!refData,
              field: "refDataId",
              value: tointeger(property(local!clauseSetContext, fv!item))
            )
          )
        }
      )
    }
  )
)

_a-0000ea70-53bb-8000-9cb1-011c48011c48_.sail_code:
/*This rule identifies a clause set as valid only when */
/*the following required/mandatory fields are available: 1.reftype 2.refCategory 3.setNumber 4. categoryNumber */
/*and required validation on the following fields: 1.Modification/AmendmentNumber for duplicate entry */
and(
  /* Solicitation or Award */
  a!isNotNullOrEmpty(
    index(ri!clauseSetContext, "refTypeId", null)
  ),
  /* Award ID or Solicitation ID */
  a!isNotNullOrEmpty(
    index(ri!clauseSetContext, "setNumber", null)
  ),
  /* Valid Category IDs check for New or Amendment/Modification */
  if(
    a!isNullOrEmpty(
      index(
        ri!clauseSetContext,
        "refCategoryId",
        null
      )
    ),
    false(),
    a!match(
      value: tointeger(
        index(ri!clauseSetContext, "refTypeId", null)
      ),
      equals: cons!AS_GCM_REF_ID_CLAUSE_SET_TYPE_AWARD,
      then: contains(
        {
          cons!AS_GCM_REF_ID_AWARD_CATEG_NEW,
          cons!AS_GCM_REF_ID_CATEGORY_MODIFICATION
        },
        index(
          ri!clauseSetContext,
          "refCategoryId",
          null
        )
      ),
      equals: cons!AS_GCM_REF_ID_CLAUSE_SET_TYPE_SOLICITATION,
      then: contains(
        {
          cons!AS_GCM_REF_ID_CATEGORY_NEW_SOLICITATION,
          cons!AS_GCM_REF_ID_CATEGORY_AMENDMENT
        },
        index(
          ri!clauseSetContext,
          "refCategoryId",
          null
        )
      ),
      default: false()
    )
  ),
  /*Category Number*/
  if(
    or(
      tointeger(
        index(
          ri!clauseSetContext,
          "refCategoryId",
          null
        )
      ) = cons!AS_GCM_REF_ID_CATEGORY_AMENDMENT,
      tointeger(
        index(
          ri!clauseSetContext,
          "refCategoryId",
          null
        )
      ) = cons!AS_GCM_REF_ID_CATEGORY_MODIFICATION
    ),
    a!isNotNullOrEmpty(
      index(
        ri!clauseSetContext,
        "categoryNumber",
        null
      )
    ),
    true()
  ),
  /* Category Number when Category is Amendment/Modificaton */
  /* The following logic will check field level validation on Modification/Amendment Number including duplicate check */
  a!isNullOrEmpty(
    tostring(
      rule!AS_GCM_VD_duplicateValidationForSetNumbAndCategNumbCombo(
        clauseSet: 'type!{urn:com:appian:types:AS:GCM}AS_GCM_ClauseSet'(
          setNumber: index(ri!clauseSetContext, "setNumber", null),
          categoryNumber: index(
            ri!clauseSetContext,
            "categoryNumber",
            null
          )
        )
      )
    )
  )
)

