if(
  /* retain the clause set clause status if any below boolean is true() */
  or(
    ri!isCopyFillins,
    ri!isAmendmentModification,
    ri!isCreateAwardFromSolicitation
  ),
  ri!statusId,
  if(
    and(
      or(ri!isFillin, ri!isEditable),
      /*Validating IBR clauses based on "Ignore IBR Validation Errors" toggle*/
      if(
        rule!AS_GCM_IBRValidationDisabled_wrapper(),
        ri!formatId = cons!AS_GCM_REF_ID_FORMAT_FULL_TEXT,
        {}
      )
    ),
    if(
      a!isNullOrEmpty(ri!fillinStats),
      cons!AS_GCM_REF_ID_CLAUSE_SET_CLAUSE_STATUS_INCOMPLETE,
      /* check if all the fillins are completed */
      if(
        ri!fillinStats.totalFillinCount = ri!fillinStats.completedFillinCount,
        cons!AS_GCM_REF_ID_CLAUSE_SET_CLAUSE_STATUS_COMPLETE,
        cons!AS_GCM_REF_ID_CLAUSE_SET_CLAUSE_STATUS_INCOMPLETE
      )
    ),
    cons!AS_GCM_REF_ID_CLAUSE_SET_CLAUSE_STATUS_COMPLETE
  )
)

