  local!datasubset: todatasubset(
    /* Output Modifier   */
    rule!AS_GCM_updateDataByModifierMaps(
      data: ri!data,
      modifierMaps: ri!modifierMaps
    ),
    ri!pagingInfo
  ),
  a!match(
    value: ri!returnType,
    equals: cons!AS_CO_ENUM_QE_RETURN_TYPE_DATA_SUBSET,
    then: local!datasubset,
    /* When the data is null then just return the same    */
    whenTrue: rule!AS_CO_UT_isBlank(ri!data),
    then: ri!data,
    /* When the return type is SINGLE_OBJECT then type cast to its corresponding single object type   */
    equals: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT,
    then: rule!AS_CO_UT_cast(
      typeOf: a!flatten(ri!data)[1],
      value: index(local!datasubset.data, 1, null)
    ),
    default: rule!AS_CO_UT_cast(
      typeOf: ri!data,
      value: local!datasubset.data
    )
  )
)

