  local!oldValue: rule!AS_CO_UT_toString(
    value: rule!AS_CO_UT_indexPath(
      cdt: ri!originalObject,
      field: ri!simpleFieldName
    )
  ),
  local!newValue: rule!AS_CO_UT_toString(
    value: rule!AS_CO_UT_indexPath(
      cdt: ri!newObject,
      field: ri!simpleFieldName
    )
  ),
  if(
    rule!AS_CO_UT_compare(
      value1: local!oldValue,
      value2: local!newValue
    ),
    {},
    rule!AS_CO_UT_dynamicDictionary(
      fieldValuePairs: {
        {
          field: "fieldName",
          value: ri!simpleFieldName
        },
        { field: "oldValue", value: local!oldValue },
        { field: "newValue", value: local!newValue }
      }
    )
  )
)

