_a-0000e5bc-4a9a-8000-9bbc-011c48011c48_.sail_code:
/* This rule is used to construct the action only audit data (Simple / Complex field changes will be null)*/
rule!AS_CO_UT_dynamicDictionary(
  fieldValuePairs: {
    {
      field: ri!auditParams.auditConfig.idField,
      value: index(
        ri!auditParams.newObjects,
        ri!auditParams.auditConfig.idField,
        null
      )
    },
    { field: "username", value: ri!initiator },
    { field: "timestamp", value: now() },
    {
      field: "auditActionCode",
      value: ri!auditParams.externalAction
    },
    if(
      rule!AS_CO_UT_isNotBlank(
        ri!auditParams.auditConfig.foreignIdField
      ),
      {
        field: ri!auditParams.auditConfig.foreignIdField,
        value: index(
          ri!auditParams.newObjects,
          ri!auditParams.auditConfig.foreignIdField,
          null
        )
      },
      {}
    )
  }
)

_a-0000e534-0cc8-8000-9bb4-011c48011c48_.sail_code:
if(
  rule!AS_CO_UT_isBlank(ri!text),
  if(ri!showHyphen = true, "-", ""),
  or(
    rule!AS_CO_UT_isBlank(ri!shortenToLen),
    ri!shortenToLen = 0
  ),
  ri!text,
  a!localVariables(
    local!shortenIfLongerThanLen: rule!AS_CO_UT_replaceNull(
      nullableValue: ri!shortenIfLongerThanLen,
      replacementValue: ri!shortenToLen
    ),
    local!showLess: true,
    {
      if(
        len(ri!text) <= local!shortenIfLongerThanLen,
        ri!text,
        {
          #"SYSTEM_SYSRULES_richTextItem_v1"(
            text: if(
              local!showLess = true,
              trim(
                rule!AS_CO_UT_left(text: ri!text, numChars: ri!shortenToLen)
              ),
              ri!text
            ),
            color: ri!textColor,
            size: ri!textSize
          ),
          #"SYSTEM_SYSRULES_richTextItem_v1"(
            color: ri!linkColor,
            text: if(
              local!showLess = true,
              a!defaultValue(
                value: ri!showMoreText,
                default: "...show more"
              ),
              a!defaultValue(
                value: ri!showLessText,
                default: "...show less"
              )
            ),
            link: #"SYSTEM_SYSRULES_dynamicLink"(
              saveInto: a!save(
                local!showLess,
                if(local!showLess = true, false, true)
              )
            ),
            linkStyle: a!defaultValue(
              value: ri!linkStyle,
              default: "STANDALONE"
            ),
            size: ri!linkTextSize,
            style: ri!linkTextStyle
          )
        }
      )
    }
  )
)

_a-0000e415-3035-8000-9bb2-011c48011c48_.sail_code:
