_a-0000e67d-efac-8000-9bcd-011c48011c48_.sail_code:
/*

PLEASE REFER TO THE BOTTOM OF THIS RULE FOR INSTRUCTIONS
*/
if(
  or(
    any(
      rule!AS_CO_UT_isBlank,
      { ri!entity, ri!pagingInfo }
    ),
    ri!executeWhen = false
  ),
  null,
  /*Testing batching inside (Might be removed later)
  Batching should only be applied when the batchSize is -1
  */
  if(
    and(
      ri!applyBatching = true,
      ri!pagingInfo.batchSize =- 1
    ),
    a!localVariables(
      local!batch: rule!AS_CO_UT_replaceNull(
        nullableValue: ri!batchSize,
        replacementValue: 10
      ),
      local!totalCount: #"SYSTEM_SYSRULES_queryEntity_v1"(
        entity: ri!entity,
        query: #"SYSTEM_SYSRULES_query"(
          pagingInfo: ri!pagingInfo,
          logicalExpression: #"SYSTEM_SYSRULES_queryLogicalExpression"(
            operator: "AND",
            logicalexpressions: {
              #"SYSTEM_SYSRULES_queryLogicalExpression"(
                operator: "AND",
                ignoreFiltersWithEmptyValues: true,
                filters: rule!AS_CO_UT_replaceNull(
                  nullableValue: ri!filters,
                  replacementValue: {}
                )
              ),
              rule!AS_CO_UT_replaceNull(
                nullableValue: ri!logicalExpression,
                replacementValue: {}
              )
            }
          )
        ),
        fetchtotalcount: true
      ).totalCount,
      local!noOfBatches: floor(local!totalCount / local!batch),
      local!lastBatch: local!totalCount - (local!noOfBatches * local!batch),
      local!lastBatchPaging: if(
        rule!AS_CO_UT_isBlank(local!lastBatch),
        null,
        #"SYSTEM_SYSRULES_pagingInfo"(
          startIndex: 1 + (local!noOfBatches * local!batch),
          batchSize: local!lastBatch
        )
      ),
      local!pagingInfo: #"SYSTEM_SYSRULES_forEach"(
        items: enumerate(local!noOfBatches),
        expression: {
          #"SYSTEM_SYSRULES_pagingInfo"(
            startIndex: 1 + (fv!item * local!batch),
            batchSize: local!batch
          )
        }
      ),
      a!flatten(
        #"SYSTEM_SYSRULES_forEach"(
          items: if(
            rule!AS_CO_UT_isBlank(local!lastBatch),
            local!pagingInfo,
            { local!pagingInfo, local!lastBatchPaging }
          ),
          expression: #"SYSTEM_SYSRULES_queryEntity_v1"(
            entity: ri!entity,
            query: #"SYSTEM_SYSRULES_query"(
              pagingInfo: fv!item,
              logicalExpression: #"SYSTEM_SYSRULES_queryLogicalExpression"(
                operator: "AND",
                logicalexpressions: {
                  #"SYSTEM_SYSRULES_queryLogicalExpression"(
                    operator: "AND",
                    ignoreFiltersWithEmptyValues: true,
                    filters: rule!AS_CO_UT_replaceNull(
                      nullableValue: ri!filters,
                      replacementValue: {}
                    )
                  ),
                  rule!AS_CO_UT_replaceNull(
                    nullableValue: ri!logicalExpression,
                    replacementValue: {}
                  )
                }
              ),
              selection: if(
                rule!AS_CO_UT_isBlank(ri!selectionColumns),
                {},
                #"SYSTEM_SYSRULES_querySelection"(
                  columns: #"SYSTEM_SYSRULES_forEach"(
                    items: ri!selectionColumns,
                    expression: #"SYSTEM_SYSRULES_queryColumn"(field: fv!item)
                  )
                )
              )
            )
          ).data
        )
      )
    ),
    #"SYSTEM_SYSRULES_queryEntity_v1"(
      entity: ri!entity,
      query: #"SYSTEM_SYSRULES_query"(
        pagingInfo: ri!pagingInfo,
        logicalExpression: #"SYSTEM_SYSRULES_queryLogicalExpression"(
          operator: "AND",
          logicalexpressions: {
            #"SYSTEM_SYSRULES_queryLogicalExpression"(
              operator: "AND",
              ignoreFiltersWithEmptyValues: true,
              filters: rule!AS_CO_UT_replaceNull(
                nullableValue: ri!filters,
                replacementValue: {}
              )
            ),
            rule!AS_CO_UT_replaceNull(
              nullableValue: ri!logicalExpression,
              replacementValue: {}
            )
          }
        ),
        selection: if(
          rule!AS_CO_UT_isBlank(ri!selectionColumns),
          {},
          #"SYSTEM_SYSRULES_querySelection"(
            columns: #"SYSTEM_SYSRULES_forEach"(
              items: ri!selectionColumns,
              expression: #"SYSTEM_SYSRULES_queryColumn"(field: fv!item)
            )
          )
        )
      ),
      fetchtotalcount: true
    )
  )
)/*This query entity can be used to get the data subset with selection applied. For now, this returns only data subset. No assertions added to the TC for now.

Required fields:
1. Entity: 
- This is the data store entity you are querying
-- e.g. cons!AS_DATA_STORE_ENTITY_CASES
2. Paging Info:
- Paging info to apply to the query, built with a!pagingInfo() or derived from fv!pagingInfo in grids*/

_a-0000e643-f7f1-8000-9bca-011c48011c48_.sail_code:
a!localVariables(
  local!clause: rule!AS_CO_UT_captureMetadata(
    cdt: rule!AS_CO_UT_updateCdtFieldsByFieldValuePairs(
      cdt: ri!clauseSetClause,
      fieldValuePairs: {
        {
          field: "refUsage",
          value: if(
            tointeger(
              rule!AS_CO_UT_indexPath(
                cdt: ri!clauseSetClause,
                field: "refUsage.refDataId"
              )
            ) = cons!AS_GCM_REF_ID_USAGE_REQUIRED,
            rule!AS_GCM_QR_getRefData(
              refDataId: cons!AS_GCM_REF_ID_USAGE_EXCLUDED
            )[1],
            ri!clauseSetClause.refUsage
          )
        },
        { field: "isRemovedManually", value: true },
        { field: "isActive", value: false },
        {
          field: "refReasonForRemoval",
          value: ri!clauseSetClause.refReasonForRemoval
        },
        {
          field: "justificationForRemoval",
          value: ri!clauseSetClause.justificationForRemoval
        },
        /*Updatting isActive false and Capturing metadata for the removed clause section*/
        {
          field: "section",
          value: rule!AS_CO_UT_captureMetadata(
            cdt: rule!AS_CO_UT_updateCdtFieldsByFieldValuePairs(
              cdt: ri!clauseSetClause.section,
              fieldValuePairs: { field: "isActive", value: false() }
            ),
            modifiedBy: ri!initiator
          )
        }
      }
    ),
    modifiedBy: ri!initiator
  ),
  local!clauseText: rule!AS_CO_UT_captureMetadata(
    cdt: rule!AS_CO_UT_updateCdtFields(
      cdt: rule!AS_GCM_QE_getClauseSetClauseTexts(
        returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT,
        setId: ri!clauseSetClause.setId,
        relationId: ri!clauseSetClause.relationId,
        executeWhen: true()
      ),
      fields: "isActive",
      newValues: false
    ),
    modifiedBy: ri!initiator
  ),
  {
    {
      entity: cons!AS_GCM_ENT_CLAUSE_SET_CLAUSE,
      data: local!clause
    },
    {
      entity: cons!AS_GCM_ENT_CLAUSE_SET_CLAUSE_TEXT,
      data: local!clauseText
    }
  }
)

