if(
  rule!AS_CO_UT_isBlank(ri!clauseText),
  "",
  a!localVariables(
    /*To get the balanced highlight tag from the clause text */
    local!highlightText: rule!AS_GCM_extractBalancedTagThatHasGivenText(
      text: ri!clauseText,
      uniqueTextInTag: "background-color:"
    ),
    /* To fetch the background-color form the highlight tag */
    local!highlightPropertyArray: regexallmatches(
      "background-color:(.*?);(" & char(32) & ")?",
      joinarray(local!highlightText, ""),
      "gms"
    ),
    /* Removing the hightlights from the tag like <span hidden> with cite tag, <span> and with other style formatting*/
    local!removeHighlights: #"SYSTEM_SYSRULES_forEach"(
      items: local!highlightText,
      expression: concat(
        a!localVariables(
          local!split: split(
            substitute(
              fv!item,
              local!highlightPropertyArray[fv!index],
              ""
            ),
            "</"
          ),
          #"SYSTEM_SYSRULES_forEach"(
            items: local!split,
            expression: if(
              fv!isFirst,
              regexreplaceall(
                "(<span style="""">| style="""")",
                fv!item,
                ""
              ),
              and(
                fv!isLast,
                find("<span style="""">", local!split[1])
              ),
              substitute(fv!item, "span>", ""),
              "</" & fv!item
            )
          )
        )
      )
    ),
    /* Substitue the removed highlight tag text with the cluase text using reduce */
    regexreplaceall(
      cons!AS_GCM_TXT_REGEX_FOR_HIDDEN_SPAN_TAG_HAVING_CLASS_PARAMETER,
      rule!AS_GCM_substituteListFromTextUsingReduce(
        text: ri!clauseText,
        find: local!highlightText,
        replaceWith: local!removeHighlights
      ),
      ""
    )
  )
)

