if(
  and(
    a!isNotNullOrEmpty(ri!clauseText),
    ri!isUpdateClauseText,
    find("</cite>", ri!clauseText) <> 0
  ),
  a!localVariables(
    /* look for the fillin metadata hidden tag */
    local!hiddenTag: regexfirstmatch(
      cons!AS_GCM_TXT_REGEX_FOR_HIDDEN_SPAN_TAG_FILLIN_METADATA,
      ri!clauseText,
      "gm"
    ),
    /*Extracting all tables from the clause text*/
    local!tableFillinCount: length(
      regexallmatches(
        "<table.*?</cite>.*?</table>",
        ri!clauseText
      )
    ),
    /* get all the simple fillins (Single line, Multiline, Checkbox) expect the table fillins */
    local!allSimpleFillins: #"SYSTEM_SYSRULES_forEach"(
      items: regexallmatches(
        "<cite.*?</cite>",
        regexreplaceall("<table.*?</table>", ri!clauseText, "")
      ),
      expression: rule!AS_GCM_UT_findFillIns(fillInText: fv!item)
    ),
    /* get the auto populate values for the fillins */
    local!autoPopulateValue: rule!AS_GCM_QR_getFillInRecDetails(
      returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
      fillInRecFillInUuid: index(local!allSimpleFillins, "uuid", null),
      isAutoPopulated: true(),
      executeWhen: a!isNotNullOrEmpty(
        index(local!allSimpleFillins, "uuid", null)
      )
    ),
    local!recFillins: rule!AS_GCM_filterRecords(
      records: local!allSimpleFillins,
      field: "uuid",
      value: local!autoPopulateValue[recordType!AS GCM Fill In Recommendation.fillInRecFillInUuid]
    ),
    /* update the cite tag with the auto populated values */
    local!updatedFillins: #"SYSTEM_SYSRULES_forEach"(
      items: local!recFillins,
      expression: a!localVariables(
        local!value: regexreplaceall(
          "<cite.*?>|</cite>",
          index(fv!item, "cite", ""),
          ""
        ),
        local!autoPopulatedValue: rule!AS_CO_UT_cast(
          type: recordType!AS GCM Fill In Recommendation,
          value: rule!AS_GCM_filterRecords(
            records: local!autoPopulateValue,
            field: recordType!AS GCM Fill In Recommendation.fillInRecFillInUuid,
            value: index(fv!item, "uuid", "")
          )
        ),
        #"urn:appian:function:v1:a:update"(
          data: fv!item,
          index: "cite",
          value: substitute(
            index(fv!item, "cite", ""),
            local!value,
            tostring(
              a!defaultValue(
                value: local!autoPopulatedValue[recordType!AS GCM Fill In Recommendation.fillInStaticRec],
                default: rule!AS_GCM_BL_dynamicFillinRecommendation(
                  bundle: ri!bundle,
                  clauseSet: ri!clauseSet,
                  fillInDynmaicRecId: local!autoPopulatedValue[recordType!AS GCM Fill In Recommendation.fillInDynamicRecId]
                ),
                /* retain the cite tag with existing value */
                default: local!value
              )
            )
          )
        )
      )
    ),
    /* Get the fillins which are updated with auto populated values as all the dynamicRecFillIn will not be updated all the time*/
    local!captureMetaDataFillins: difference(local!updatedFillins, local!recFillins),
    local!updatedClauseText: rule!AS_GCM_substituteListFromTextUsingReduce(
      text: ri!clauseText,
      find: index(local!recFillins, "cite", null()),
      replaceWith: index(local!updatedFillins, "cite", null())
    ),
    local!metaDataAndStats: rule!AS_GCM_constructClauseTextMetaDataFromClauseText(
      fillinsUuid: index(local!captureMetaDataFillins, "uuid", ""),
      completedBy: cons!AS_GCM_TXT_FILL_IN_COMPLETED_BY_SYSTEM_AGENCY,
      totalFillinCount: length(local!allSimpleFillins) + local!tableFillinCount,
      completedFillinCount: length(local!captureMetaDataFillins),
      fillableTableCount: local!tableFillinCount,
      /*0 is hardcoded here because the table fill-ins cannot be auto-populated during creation of clause set  */
      completedFillableTableCount: 0,
      totalSimpleFillins: length(local!allSimpleFillins),
      completedSimpleFillins: length(local!captureMetaDataFillins),
      totalCiteTagsCount: length(
        regexallmatches("<cite.*?</cite>", ri!clauseText)
      )
    ),
    if(
      a!isNotNullOrEmpty(local!hiddenTag),
      substitute(
        local!updatedClauseText,
        local!hiddenTag,
        local!metaDataAndStats
      ),
      concat(
        local!updatedClauseText,
        local!metaDataAndStats
      )
    )
  ),
  ri!clauseText
)

